INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34377, 'Ciclesonide', 'Atazanavir', 'Moderate', 'Coadministration of inhaled ciclesonide with potent inhibitors of CYP450 3A4 may increase systemic bioavailability of the pharmacologically active metabolite, des-ciclesonide, which is a substrate of the isoenzyme.', NULL, 'Caution is advised if ciclesonide is prescribed with potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised if ciclesonide is prescribed with potent CYP450 3A4 inhibitors. Alternatively, a less potent, less lipophilic, and/or shorter-acting agent such as beclomethasone may be considered. Beclomethasone is also less dependent on CYP450 3A4 metabolism. Patients should be monitored for systemic glucocorticoid effects including symptoms of hypercorticism (e.g., acne, easy bruising, moon face, edema, hirsutism, buffalo hump, skin striae, irregular menstruations), adrenal suppression (which reduces patient''s ability to respond to stress situations), immunosuppression, osteoporosis, glucose intolerance, and exacerbation of diabetes mellitus.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Velpatasvir, More', 'Mometasone furoate, Umeclidinium, Aclidinium, Glycopyrronium, Ipratropium, Cromoglicic acid, Nedocromil, Tiotropium, Beclomethasone dipropionate, Mupirocin, Mometasone furoate, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34430/', '[1] "Product Information. Alvesco (ciclesonide)." Nycomed USA, Florham Park, NJ.[2] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.em