INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34383, 'Citalopram', 'Atazanavir', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 is not expected to significantly affect the pharmacokinetics of citalopram or escitalopram, both of which are partially metabolized by the isoenzyme. Administration of racemic citalopram (40 mg) in combination with the potent CYP450 3A4 inhibitor ketoconazole (200 mg) decreased the ketoconazole peak plasma concentration (Cmax) by 21% and systemic exposure (AUC) by 10%, but did not significantly affect the pharmacokinetics of citalopram.', NULL, '-', NULL, '-', 'Metabolism', 'Phenelzine, Desipramine, Tranylcypromine, Desvenlafaxine, Maprotiline, Fluoxetine, Isocarboxazid, Oxitriptan, Milnacipran, Vortioxetine, Nortriptyline, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Dolutegravir, Brincidofovir, Remdesivir, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34436/', '[1] "Product Information. Lexapro (escitalopram)." Forest Pharmaceuticals, St. Louis, MO.[2] "Product Information. Celexa (citalopram)." Forest Pharmaceuticals, St. Louis, MO.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34384, 'Clarithromycin', 'Atazanavir', 'Moderate', 'Coadministration with atazanavir may significantly increase the plasma concentration of clarithromycin but decrease that of the active metabolite, 14-OH clarithromycin. The mechanism is atazanavir inhibition of CYP450 3A4, the isoenzyme responsible for the metabolism of clarithromycin.', NULL, 'Because increased plasma concentrations of clarithromycin may cause prolongation of the QT interval of the electrocardiogram, a dose reduction of clarithromycin by 50% is recommended for patients with moderate renal insufficiency (CrCl 30 to 60 mL/min) and a dose reduction by 75% is recommended for patients with severe renal insufficiency (CrCl less than 30 mL/min).', NULL, 'Because increased plasma concentrations of clarithromycin may cause prolongation of the QT interval of the electrocardiogram, a dose reduction of clarithromycin by 50% is recommended for patients with moderate renal insufficiency (CrCl 30 to 60 mL/min) and a dose reduction by 75% is recommended for patients with severe renal insufficiency (CrCl less than 30 mL/min). The daily dose of clarithromycin should not exceed 1000 mg during concomitant use with protease inhibitors. In addition, because plasma concentrations of the active metabolite is significantly reduced, alternative therapy should be considered for indications other than infections due to Mycobacterium avium complex. Some authorities recommend avoiding the use of the fixed combination atazanavir-cobicistat with clarithromycin.', 'Metabolism', 'Glecaprevir, Tenofovir alafenamide, Cabotegravir, Lenacapavir, Velpatasvir, Voxilaprevir, Maribavir, Pibrentasvir, Rimantadine, Elvitegravir, Valganciclovir, More', 'Tinidazole, Levofloxacin, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Amoxicillin, Lincomycin, Dirithromycin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34437/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.[3] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.[4] Cheng KL, Nafziger AN, Peloquin CA, Amsden GW "Effect of grapefruit juice on clarithromycin pharmacokinetics." Antimicrob Agents Chemother 42 (1998): 927-9', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34385, 'Clindamycin', 'Atazanavir', 'Moderate', 'Coadministration with strong CYP450 3A4 inhibitors may increase plasma concentrations and the risk of adverse effects of clindamycin, which is metabolized predominantly by the isoenzyme.', NULL, 'Caution is advised if clindamycin is used concomitantly with strong CYP450 3A4 inhibitors.', NULL, 'Caution is advised if clindamycin is used concomitantly with strong CYP450 3A4 inhibitors. The possibility of adverse reactions to clindamycin should be considered during coadministration with strong CYP450 3A4 inhibitors. Dosage adjustments as well as clinical and laboratory monitoring of clindamycin should be considered whenever a strong CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Lenacapavir, Velpatasvir, Rilpivirine, Etravirine, More', 'Minocycline, Vitamin A, Isotretinoin, Chloramphenicol, Fluorometholone, Azelaic acid, Adapalene, Lincomycin, Dirithromycin, Tinidazole, Ascorbic acid, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34438/', '[1] "Product Information. Cleocin (clindamycin)." Pharmacia and Upjohn, Kalamazoo, MI.[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34386, 'Clomipramine', 'Atazanavir', 'Moderate', 'Product labeling for certain protease inhibitors (PIs) such as amprenavir and atazanavir states that serious and/or life-threatening drug interactions may occur with tricyclic antidepressants (TCAs), presumably due to PI inhibition of TCA metabolism via CYP450 3A4. High plasma levels of TCAs could conceivably lead to serious adverse reactions including QT interval prolongation and ventricular arrhythmias such as ventricular tachycardia and torsade de pointes. Moreover, many TCAs are thought to be substrates of multiple CYP450 isoenzymes, thus the effects of CYP450 3A4 inhibition alone are unknown. Some TCAs such as desipramine, doxepin, and nortriptyline are also thought to be primarily metabolized by CYP450 2D6, which is not known to be inhibited by most PIs (except ritonavir) to any significant extent.', NULL, 'Until further information is available, caution is advised if protease inhibitors must be used with certain TCAs.', NULL, 'Until further information is available, caution is advised if protease inhibitors must be used with certain TCAs. Pharmacologic response and plasma TCA levels should be monitored more closely whenever a PI is added to or withdrawn from therapy, and the TCA dosage adjusted as necessary.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 'Phenelzine, Desipramine, Tranylcypromine, Desvenlafaxine, Maprotiline, Fluoxetine, Isocarboxazid, Oxitriptan, Milnacipran, Vortioxetine, Nortriptyline, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2