INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34389, 'Cobimetinib', 'Atazanavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of cobimetinib, which is primarily metabolized by the isoenzyme. The risk and/or severity of adverse effects such as diarrhea, nausea, vomiting, stomatitis, hemorrhage, cardiomyopathy, rash, photosensitivity, retinopathy, retinal vein occlusion, liver enzyme abnormalities, and rhabdomyolysis may be increased.', NULL, 'Concomitant use of cobimetinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of cobimetinib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of cobimetinib during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Valaciclovir, Zanamivir, Rilpivirine, Dolutegravir, Oseltamivir, Acyclovir, More', 'Futibatinib, Alpelisib, Lenvatinib, Trametinib, Vandetanib, Afatinib, Dacomitinib, Tivozanib, Sorafenib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34442/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[6] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34390, 'Colchicine', 'Atazanavir', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the serum concentrations of colchicine, which is primarily metabolized by the isoenzyme. Clinical toxicity including myopathy, neuropathy, multiorgan failure, and pancytopenia may occur.', NULL, 'Due to the risk of life-threatening and fatal toxicity, concomitant use of colchicine with potent CYP450 3A4 inhibitors should generally be avoided if possible.', NULL, 'Due to the risk of life-threatening and fatal toxicity, concomitant use of colchicine with potent CYP450 3A4 inhibitors should generally be avoided if possible. Otherwise, caution is advised. In patients with normal renal and hepatic function, the dosage of colchicine should be reduced when used with potent CYP450 3A4 inhibitors or within 14 days of using them. For the treatment of acute gout flares, the recommended dose is 0.6 mg for one dose, followed by 0.3 mg one hour later. Administration should not be repeated for at least three days. For the prophylaxis of gout flares, the adjusted dosage should be 0.3 mg once a day if the original regimen was 0.6 mg twice a day, and 0.3 mg once every other day if the original regimen was 0.6 once a day. For the treatment of familial Mediterranean fever, the maximum dosage of colchicine is 0.6 mg/day (may be given as 0.3 mg twice a day) when used in the presence of potent CYP450 3A4 inhibitors. Patients should be advised to contact their physician if they experience symptoms of toxicity such as abdominal pain, nausea, vomiting, diarrhea, fatigue, myalgia, asthenia, hyporeflexia, paresthesia, and numbness. Coadministration of colchicine with potent CYP450 3A4 inhibitors in patients with renal or hepatic impairment is considered contraindicated.', 'Metabolism', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Rilpivirine, Dolutegravir, Oseltamivir, More', 'Pegloticase, Febuxostat, Rasburicase, Probenecid', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34443/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Dogukan A, Oymak FS, Taskapan H, Guven M, Tokgoz B, Utas C "Acute fatal colchicine intoxication in a patient on continuous ambulatory peritoneal dialysis (CAPD). Possible role of clarithromycin administration." Clin Nephrol 55 (2001): 181-2[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] McKinnell J, Tayek JA "Short term treatment with clarithromycin resulting in colchicine-induced rhabdomyolysis." J Clin Rheumatol 15 (2009): 303-5[5] Cheng VC, Ho PL, Yuen KY "Two probable cases of serious drug interaction between clarithromycin and colchicine." South Med J 98 (2005): 811-3[6] "Colchicine: serious interactions." Prescrire Int 17 (2008): 151-3[7] Caraco Y, Putterman C, Rahamimov R, Ben-Chetrit E "Acute colchicine intoxication: possible role of erythromycin administration." J Rheumatol 19 (1992): 494-6[8] Putterman C, Ben-Chetrit E, Caraco Y, Levy M "Colchicine intoxication: clinical pharmacology, risk factors, features, and management." Semin Arthritis Rheum 21 (1991): 143-55[9] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[10] Rollot F, Pajot O, Chauvelot-Moachon L, Nazal EM, Kelaidi C, Blanche P "Acute colchicine intoxication during clarithromycin administration." Ann Pharmacother 38 (2004): 2074-7[11] "Product Information. Colcrys (colchicine)." AR Scientific Inc, Philadelphia, PA.[12] van der Velden W, Huussen J, Ter Laak H, de Sevaux R "Colchicine-induced neuromyopathy in a patient with chronic renal failure: t