INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34400, 'Darifenacin', 'Atazanavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of darifenacin, which is partially metabolized by the isoenzyme. Darifenacin is also metabolized by CYP450 2D6, but 3A4 is the major pathway of metabolism in so-called poor metabolizers of 2D6.', NULL, 'The dosage of darifenacin should not exceed 7.', NULL, 'The dosage of darifenacin should not exceed 7.5 mg/day when used with potent CYP450 3A4 inhibitors. Some authorities recommend avoiding concomitant use of darifenacin during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Apomorphine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Papaverine, Phenyl salicylate, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34453/', '[1] Niwa T, Shiraga T, Takagi A "Effect of antifungal drugs on cytochrome P450 (CYP) 2C9, CYP2C19, CYP3A4 activities in human liver microsomes." Biol Pharm Bull 28 (2005): 1805-8[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] "Product Information. Enablex (darifenacin)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2