INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34411, 'Didanosine', 'Atazanavir', 'Moderate', 'INTERVAL: Concurrent administration of didanosine buffered tablets or powder or the pediatric oral solution may significantly decrease the oral bioavailability of atazanavir. Atazanavir solubility decreases with increasing pH, thus buffering agents in the didanosine formulations may interfere with dissolution of the drug.', NULL, 'Atazanavir should be administered, with food, 2 hours before or 1 hour after didanosine buffered formulations.', NULL, 'Atazanavir should be administered, with food, 2 hours before or 1 hour after didanosine buffered formulations. Didanosine delayed-release capsules are not buffered and are not expected to cause this interaction. However, because didanosine should be administered on an empty stomach, the capsules should also be administered at different times from atazanavir.', 'Absorption', 'Cabotegravir, Amprenavir, Zanamivir, Velpatasvir, Rilpivirine, Oseltamivir, Cobicistat, Remdesivir, Famciclovir, Darunavir, Maribavir, More', 'Paritaprevir, Glecaprevir, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Rilpivirine, Oseltamivir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34464/', '[1] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Videx (didanosine)." Bristol-Myers Squibb, Princeton, NJ.[4] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.', 1767369485);
