INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34485, 'Imipramine', 'Atazanavir', 'Moderate', 'Product labeling for certain protease inhibitors (PIs) such as amprenavir and atazanavir states that serious and/or life-threatening drug interactions may occur with tricyclic antidepressants (TCAs), presumably due to PI inhibition of TCA metabolism via CYP450 3A4. High plasma levels of TCAs could conceivably lead to serious adverse reactions including QT interval prolongation and ventricular arrhythmias such as ventricular tachycardia and torsade de pointes. Moreover, many TCAs are thought to be substrates of multiple CYP450 isoenzymes, thus the effects of CYP450 3A4 inhibition alone are unknown. Some TCAs such as desipramine, doxepin, and nortriptyline are also thought to be primarily metabolized by CYP450 2D6, which is not known to be inhibited by most PIs (except ritonavir) to any significant extent.', NULL, 'Until further information is available, caution is advised if protease inhibitors must be used with certain TCAs.', NULL, 'Until further information is available, caution is advised if protease inhibitors must be used with certain TCAs. Pharmacologic response and plasma TCA levels should be monitored more closely whenever a PI is added to or withdrawn from therapy, and the TCA dosage adjusted as necessary.', 'Metabolism', 'Phenelzine, Desipramine, Tranylcypromine, Desvenlafaxine, Maprotiline, Fluoxetine, Isocarboxazid, Oxitriptan, Milnacipran, Vortioxetine, Nortriptyline, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Etravirine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.sc