INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34595, 'Ozanimod', 'Atazanavir', 'Moderate', 'The risk of transient bradycardia and atrioventricular (AV) block may be increased during initiation of ozanimod treatment in patients receiving other drugs that slow heart rate or AV conduction such as beta-blockers, certain calcium channel blockers (e.g., diltiazem, verapamil), and digitalis. Ozanimod can cause a decrease in heart rate during initiation of therapy.', NULL, 'Ozanimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate.', NULL, 'Ozanimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate. In addition, advice from a cardiologist should be sought if coadministration of ozanimod is considered in patients with significant QT prolongation (QTcF greater than 450 msec in males or 470 msec in females), patients with arrhythmias requiring treatment with Class 1a or Class III antiarrhythmic agents, patients with ischemic heart disease, heart failure, history of cardiac arrest or myocardial infarction, cerebrovascular disease, or uncontrolled hypertension. Use of ozanimod in patients with a history of second-degree Mobitz type II or higher AV block, sick-sinus syndrome, or sinoatrial heart block is considered contraindicated unless the patient has a functioning pacemaker.', 'Synergism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Amprenavir, Cabotegravir, Valaciclovir, Zanamivir, Telaprevir, Velpatasvir, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34648/', '[1] "Product Information. Zeposia (ozanimod)." Celgene Corporation, Summit, NJ.[2] "Product Information. Zeposia (ozanimod)." Celgene Corporation, Summit, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34596, 'Paclitaxel (protein-bound)', 'Atazanavir', 'Major', 'Coadministration with certain antiretroviral agents such as protease inhibitors and delavirdine may increase the plasma concentrations and pharmacologic effects of paclitaxel. The proposed mechanism is inhibition of the CYP450 3A4-mediated metabolism of paclitaxel.', NULL, 'Caution is advised if paclitaxel is required in patients receiving protease inhibitors and/or delavirdine.', NULL, 'Caution is advised if paclitaxel is required in patients receiving protease inhibitors and/or delavirdine. A lower initial dosage of paclitaxel may be appropriate. Patients should be closely monitored for the development of dose-related paclitaxel toxicity such as myelosuppression, stomatitis, arthralgia, myalgia, visual disturbances and peripheral neuropathy, and the paclitaxel dosage further adjusted as necessary. Use of repaglinide with the fixed combination of atazanavir-cobicistat is not recommended.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34649/', '[1] Bundow D, Aboulafia DM "Potential drug interaction with paclitaxel and highly active antiretroviral therapy in two patients with AIDS-associated Kaposi sarcoma." Am J Clin Oncol 27 (2004): 81-4[2] "Product Information. Taxol (paclitaxel)." Bristol-Myers Squibb, Princeton, NJ.[3] "Product Information. Taxotere (docetaxel)." Rhone-Poulenc Rorer, Collegeville, PA.[4] Panday VRN, Hoetelmans RMW, vanHeeswijk RPG, Meenhorst PL, Inghels M