INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34632, 'Raltegravir', 'Atazanavir', 'Minor', 'Coadministration with inhibitors of uridine diphosphate glucuronosyltransferase (UGT) 1A1 such as atazanavir, nilotinib, or sorafenib may increase the plasma concentrations of raltegravir, which is primarily metabolized by UGT1A1-mediated glucuronidation. However, concurrent use of atazanavir/ritonavir in clinical trials did not result in meaningful differences in the safety of raltegravir. Based on these data, no dosage adjustment of raltegravir is necessary when used together with atazanavir/ritonavir or other UGT1A1 inhibitors.', NULL, '-', NULL, '-', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Velpatasvir, More', 'Cabotegravir, Amprenavir, Zanamivir, Velpatasvir, Rilpivirine, Oseltamivir, Cobicistat, Remdesivir, Famciclovir, Darunavir, Maribavir, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34685/', '[1] "Product Information. Isentress (raltegravir)." Merck & Company Inc, West Point, PA.[2] "Product Information. Nexavar (sorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34633, 'Ramelteon', 'Atazanavir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 and/or 2C9 may increase the plasma concentrations and pharmacologic effects of ramelteon, which is partially metabolized by these isoenzymes.', NULL, 'Caution is advised if ramelteon is prescribed in combination with potent inhibitors of CYP450 3A4 (e.', NULL, 'Caution is advised if ramelteon is prescribed in combination with potent inhibitors of CYP450 3A4 (e.g., itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, delavirdine, nefazodone, protease inhibitors, ketolide and certain macrolide antibiotics) and/or CYP450 2C9 (e.g., fluconazole, gemfibrozil, imatinib, miconazole). A reduction in the ramelteon dosage may be necessary in patients who experience excessive sedation.', 'Metabolism', 'Remimazolam, Chloral hydrate, Flurazepam, Quazepam, Paraldehyde, Thiopental, Propiomazine, Meprobamate, Scopolamine, Dexmedetomidine, Methohexital, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34686/', '[1] "Product Information. Rozerem (ramelteon)." Takeda Pharmaceuticals America, Lincolnshire, IL.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[4] "Product Information. Rozerem (ramelteon)." Takeda Pharmaceuticals America, Lincolnshire, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34634, 'Ranitidine (bismuth citrate)', 'Atazanavir', 'Major', 'Concurrent administration of H2-receptor antagonists may decrease the oral bioavailability of atazanavir and substantially reduce its concentrations in plasma. Atazanavir solubility decreases with increasing pH, thus inhibition of gastric acid secretion may interfere with dissolution of the drug. Subtherapeutic antiretroviral drug levels may lead to reduced viral susceptibility and development of resistance.', NULL, 'When used with atazanavir/cobicistat or atazanavir/ritonavir once daily in adults, the dosage of the H2-receptor antagonist should be no more than the equivalent of famotidine 40 mg twice daily in antiretroviral treatment-naive patients and 20 mg twice daily in treatment-experienced patients.', NULL, 'When used with atazanavir/cobicistat or atazanavir/ritonavir once daily in adults, the dosage of the H2-receptor antagonist should be no more than the equivalent of famotidine 40 mg twice daily in antiretroviral treatment-naive patients and 20 mg twice daily in treatment-experienced patients. Atazanavir/cobicistat or atazanavir/ritonavir should be administered simultaneously with, and/or at least 10 hours after, the H2-receptor antagonist. If tenofovir is also prescribed, no additional precaution is necessary in treatment-naive patients, whereas the dosage of atazanavir/ritonavir should be increased to 400 mg/100 mg once a day in treatment-experienced patients. All three antiretroviral agents should be administered as a single daily dose with food. In the event that ritonavir is not tolerated by a treatment-naive patient, atazanavir may be administered at an increased dosage of 400 mg once daily at least 2 hours before and at least 10 hours after the H2-receptor antagonist.', 'Absorption', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34687/', '[1] Ray JE, Marriott D, Bloch MT, McLachlan AJ "Therapeutic drug monitoring of atazanavir: surveillance of pharmacotherapy in the clinic." Br J Clin Pharmacol 60 (2005): 291-9[2] "Drug interactions. Atazanavir and acid-reducing agents." TreatmentUpdate 18 (2006): 4[3] Agarwala S, Persson A, Eley T, et al "Effect of famotidine 20 and 40 mg dosing regimens on the bioavailability of atazanavir with ritonavir in combination with tenofovir in healthy subjects. Available from: URL: http://www.natap.org/2007/CROI/croi_62.htm." ([2007 Feb 25-7]):[4] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.[5] Klein CE, Chiu YL, Cai Y, et al. "Effects of Acid-reducing agents on the pharmacokinetics of lopinavir/ritonavir and ritonavir-boosted atazanavir." J Clin Pharmacol 48 (2008): 553-62[6] Durant J, Clevenbergh P, Garraffo R, Halfon P, Icard S, DelGiudice P, Montagne N, Schapiro JM, Dellamonica P "Importance of protease inhibitor plasma levels in HIV-infected patients treated with genotypic-guided therapy: pharmacological data from the Viradapt Study." Aids 14 (2000): 1333-9[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb, Princeton, NJ.[9] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34635, 'Ranolazine', 'Atazanavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ranolazine, which is primarily metabolized by the isoenzyme. Because ranolazine prolongs QT interval in a dose-dependent manner, high plasma levels of ranolazine may increase the risk of ventricular arrhythmias such as ventricular tachycardia, ventricular fibrillation, and torsade de pointes.', NULL, 'Concomitant use of ranolazine with potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, 'Concomitant use of ranolazine with potent CYP450 3A4 inhibitors is considered contraindicated. Some authorities consider concomitant administration of ranolazine and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Adenosine, Regadenoson, Alprostadil, Ubidecarenone', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Oseltamivir, Acyclovir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34688/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.[5] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.[6] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[7] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34636, 'Regorafenib', 'Atazanavir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of regorafenib and decrease the plasma concentrations of its active metabolites.', NULL, 'Concomitant use of regorafenib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of regorafenib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of regorafenib during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Futibatinib, Alpelisib, Lenvatinib, Trametinib, Vandetanib, Afatinib, Dacomitinib, Tivozanib, Sorafenib', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34689/', '[1] "Product Information. Stivarga (regorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[6] "Product Information. Stivarga (regorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767369485);
