INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34648, 'Ritonavir', 'Atazanavir', 'Moderate', 'Coadministration with ritonavir may significantly increase the plasma concentrations of atazanavir. The mechanism is ritonavir inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of atazanavir.', NULL, 'Patients requiring the combination should be prescribed atazanavir 300 mg with ritonavir 100 mg as a single daily dose with food.', NULL, 'Patients requiring the combination should be prescribed atazanavir 300 mg with ritonavir 100 mg as a single daily dose with food.', 'Metabolism', 'Cabotegravir, Amprenavir, Zanamivir, Velpatasvir, Rilpivirine, Oseltamivir, Cobicistat, Remdesivir, Famciclovir, Darunavir, Maribavir, More', 'Paritaprevir, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Oseltamivir, Darunavir, Maribavir, Rimantadine, Elvitegravir, Telbivudine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34701/', '[1] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34649, 'Rivaroxaban', 'Atazanavir', 'Moderate', 'Coadministration with moderate or potent inhibitors of CYP450 3A4 may increase the plasma concentrations of rivaroxaban, which is a substrate of the isoenzyme.', NULL, 'In patients with significant renal impairment, the use of rivaroxaban with moderate or potent inhibitors of CYP450 3A4 should be approached with caution.', NULL, 'In patients with significant renal impairment, the use of rivaroxaban with moderate or potent inhibitors of CYP450 3A4 should be approached with caution. Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women). Renal function should also be assessed periodically, and treatment with rivaroxaban discontinued if acute renal failure develops. Due to the lack of clinical data, rivaroxaban is not recommended in patients with CrCl below 30 mL/min when used for the prophylaxis of deep vein thrombosis and in patients with CrCl below 15 mL/min when used for reducing the risk of stroke and systemic embolism in nonvalvular atrial fibrillation.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, Desirudin, Eptifibatide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34702/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Xarelto (rivaroxaban)." Bayer Inc, Toronto, IA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34650, 'Rivastigmine', 'Atazanavir', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Memantine, Ginkgo biloba, Aducanumab', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34703/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Cognex (tacrine)." Parke-Davis, Morris Plains, NJ.[3] Tiseo PJ, Perdomo CA, Friedhoff LT "Concurrent administration of donepezil HCI and digoxin: assessment of pharmacokinetic changes." Br J Clin Pharmacol 46(Suppl 1) (1998): 40-4[4] Tavassoli N, Sommet A, Lapeyre-Mestre M, Bagheri H, Montrastruc JL "Drug interactions with cholinesterase inhibitors : an analysis of the French pharmacovigilance database and a comparison of two national drug formularies (vidal, british national formulary)." Drug Saf 30 (2007): 1063-71[5] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34651, 'Romidepsin', 'Atazanavir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of romidepsin, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised when romidepsin is used with potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised when romidepsin is used with potent CYP450 3A4 inhibitors. Patients should have complete blood cell counts, electrocardiograms, and serum electrolyte levels performed at baseline and regular intervals as recommended in the product labeling. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Etravirine, More', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Porfimer sodium, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34704/', '[1] "Product Information. Istodax (romidepsin)." Gloucester Pharmaceuticals, Cambridge, MA.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
