INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34680, 'Sotalol', 'Atazanavir', 'Major', 'Atazanavir has been shown to prolong the PR interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, digoxin, lacosamide, mefloquine, verapamil) may result in elevated risk of conduction disturbances and atrioventricular block.', NULL, 'Caution is advised if atazanavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.', NULL, 'Caution is advised if atazanavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.g., verapamil), since atazanavir is an inhibitor of that isoenzyme.', 'Synergism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Amprenavir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34733/', '[1] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Stribild (cobicistat/elvitegravir/emtricitabine/tenofov)." Gilead Sciences, Foster City, CA.[3] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34681, 'St. John''s Wort', 'Atazanavir', 'Major', 'Coadministration with St. John''s wort may significantly reduce the plasma concentrations of HIV protease inhibitors and result in a potential loss of virologic response. The mechanism is induction of CYP450 3A4 metabolism by constituents of St. John''s wort.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of HIV protease inhibitors with St.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of HIV protease inhibitors with St. John''s wort is considered contraindicated. The enzyme induction may last for up to 2 weeks after the last dose of St. John''s wort. Patients should be advised to consult with their caregivers before using any herbal or alternative medicines.', 'Metabolism', 'Lamivudine, Paritaprevir, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, Elbasvir, More', 'Phenelzine, Desipramine, Tranylcypromine, Desvenlafaxine, Maprotiline, Fluoxetine, Isocarboxazid, Oxitriptan, Milnacipran, Vortioxetine, Nortriptyline, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34734/', '[1] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[5] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[6] "Product Information. Agenerase (amprenavir)." Glaxo Wellcome, Research Triangle Pk, NC.[7] Demarles D, Gillotin C, Bonaventure-Paci S, Vincent I, Fosse S, Taburet AM "Single-dose pharmacokinetics of amprenavir coadministered with grapefruit juice." Antimicrob Agents Chemother 46 (2002): 1589-1590', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34682, 'Stiripentol', 'Atazanavir', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of stiripentol, which has been shown in vitro to undergo phase I metabolism via these pathways. Conversely, many of these inhibitors are also substrates of CYP450 1A2 and/or 3A4, and coadministration with stiripentol may alter their plasma concentrations as well. Stiripentol is both an inhibitor and inducer of CYP450 1A2 and 3A4 in vitro.', NULL, 'Caution is advised when stiripentol is used with inhibitors of CYP450 1A2 and/or 3A4 that are also substrates of these enzymatic pathways.', NULL, 'Caution is advised when stiripentol is used with inhibitors of CYP450 1A2 and/or 3A4 that are also substrates of these enzymatic pathways. Patients should be monitored for potentially increased adverse effects of stiripentol such as anorexia, nausea, vomiting, weight loss, somnolence, dizziness, confusion, difficulty concentrating, ataxia, hypotonia, tremor, hyperkinesia, dysarthria, suicidal ideation/behavior, neutropenia, and thrombocytopenia. In addition, clinical and laboratory monitoring may be appropriate for concomitant drugs whenever stiripentol is added to or withdrawn from therapy. Dosage adjustments or alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Velpatasvir, Oseltamivir, More', 'Tiagabine, Ethotoin, Brivaracetam, Rufinamide, Methsuximide, Ezogabine, Paramethadione, Perampanel, Zonisamide, Fenfluramine, Phensuximide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34735/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Diacomit (stiripentol)." Biocodex Inc, Creswell, OR.[3] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[6] "Product Information. Diacomit (stiripentol)." Biocodex Inc, Creswell, OR.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34683, 'Sufentanil', 'Atazanavir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of sufentanil, which is primarily metabolized by the isoenzyme. Increased sufentanil concentrations may enhance or prolong pharmacologic effects and potentiate the risk of central nervous system and respiratory depression.', NULL, 'Patients receiving sufentanil with potent CYP450 3A4 inhibitors should be carefully monitored for excessive central nervous system and respiratory depression, and dosage adjustments made accordingly if necessary.', NULL, 'Patients receiving sufentanil with potent CYP450 3A4 inhibitors should be carefully monitored for excessive central nervous system and respiratory depression, and dosage adjustments made accordingly if necessary.', 'Metabolism', 'Halothane, Thiopental, Remifentanil, Sodium oxybate, Isoflurane, Methohexital, Nitrous oxide, Desflurane, Propofol, Enflurane, Esketamine, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34736/', '[1] Bartkowski RR, Goldberg ME, Huffnagle S, Epstein RH "Sufentanil disposition. Is it affected by erythromycin administration?" Anesthesiology 78 (1993): 260-5[2] "Product Information. Sufenta (sufentanil)." Janssen Pharmaceutica, Titusville, NJ.[3] Tateishi T, Krivoruk Y, Ueng YF, Wood AJ, Guengerich FP, Wood M "Identification of human cytochrome P-450 3A4 as the enzyme responsible for fentanyl and sufentanil N-dealkylation." Anesth Analg 82 (199