INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (34696, 'Temsirolimus', 'Atazanavir', 'Major', 'Coadministration of temsirolimus with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of sirolimus, a major active metabolite of temsirolimus and known substrate of CYP450 3A4.', NULL, 'Concomitant use of temsirolimus with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of temsirolimus with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of intravenous temsirolimus during and for 2 weeks after treatment with itraconazole (US). If coadministration is required, the manufacturer recommends reducing the temsirolimus dosage to 12.5 mg once a week. Based on pharmacokinetic studies, this dosage is predicted to adjust the sirolimus systemic exposure (AUC) to the range observed without inhibitors. However, clinical data are lacking. Following discontinuation of the potent CYP450 3A4 inhibitor, a washout period of approximately one week should be allowed before the temsirolimus dosage is adjusted upward to the normally recommended dosage (i.e., 25 mg once a week) or the dosage used prior to initiation of the CYP450 3A4 inhibitor.', 'Metabolism', 'Lamivudine, Paritaprevir, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, Brincidofovir, More', 'Futibatinib, Alpelisib, Lenvatinib, Trametinib, Vandetanib, Afatinib, Dacomitinib, Tivozanib, Sorafenib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34749/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Torisel