INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (69507, 'Flumazenil', 'Clobazam', 'Moderate', 'The administration of flumazenil can result in convulsions in patients who have been receiving long-term treatment with benzodiazepines for epilepsy or sedation or who have recently received repeated doses of parenteral benzodiazepines. The mechanism is abrupt reversal of the benzodiazepine''s anticonvulsive effects. In addition, withdrawal seizures may occur in patients who are physically dependent on benzodiazepines.', NULL, 'The use of flumazenil is not recommended in patients with epilepsy who have been receiving long-term treatment with benzodiazepines.', NULL, 'The use of flumazenil is not recommended in patients with epilepsy who have been receiving long-term treatment with benzodiazepines. In addition, flumazenil is contraindicated in patients who have received a benzodiazepine to control potentially life-threatening conditions such as status epilepticus or increased intracranial pressure. Extreme caution, dose individualization, and close monitoring is recommended for other patients. Clinicians should be prepared to manage convulsions. Most seizures have been successfully managed with benzodiazepines, phenytoin, or barbiturates.', 'Antagonism', 'Meprobamate, Buspirone, Hydroxyzine', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Cobicistat, Deferoxamine, Levoleucovorin, Arginine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/69594/', '[1] "Product Information. Romazicon (flumazenil)." Roche Laboratories, Nutley, NJ.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between eth