INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1344394, 'Amifostine', 'Lisinopril', 'Moderate', 'Concomitant use with hypotensive agents may increase the risk and severity of hypotension associated with amifostine. Hypotension has been reported frequently in patients treated with amifostine. It is usually transient (lasting from 5 to 15 minutes) and closely associated with amifostine administration, but may persist or become evident hours after administration.', NULL, 'If medically feasible, antihypertensive medications should be interrupted 24 hours prior to amifostine administration.', NULL, 'If medically feasible, antihypertensive medications should be interrupted 24 hours prior to amifostine administration. Blood pressure should be closely monitored during and after treatment. In addition to hypotension, transient hypertension or exacerbation of preexisting hypertension may occur from intravenous hydration, discontinuation of antihypertensive medications, or other causes. Patients who continue their hypotensive medication(s) during amifostine therapy should be carefully monitored for the development of hypotension.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
