        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1344508, 'Gentamicin (topical)', 'Amikacin', 'Moderate', 'Topically applied aminoglycosides may be systemically absorbed and have potentially additive effects with systemically administered aminoglycosides. The risk of oto- and nephrotoxicity and other adverse effects of aminoglycosides may be increased.', NULL, 'Topical preparations containing an aminoglycoside should not be used with other aminoglycosides in circumstances where significant systemic absorption could occur (e.', NULL, 'Topical preparations containing an aminoglycoside should not be used with other aminoglycosides in circumstances where significant systemic absorption could occur (e.g., application to burnt or broken skin; use of large quantities or on large areas for prolonged periods). The normally recommended dosages of these products should not be exceeded.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
