        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1344528, 'Amikacin', 'Magnesium sulfate', 'Major', 'Aminoglycosides possess neuromuscular blocking activity, which may be additive with that of parenteral magnesium, potentially resulting in severe and/or prolonged respiratory depression during concomitant use. In addition, aminoglycosides may reduce serum magnesium levels.', NULL, 'Close monitoring for increased or prolonged neuromuscular blockade and respiratory depression is recommended during concomitant use, especially in patients with renal impairment and/or with elevated magnesium levels.', NULL, 'Close monitoring for increased or prolonged neuromuscular blockade and respiratory depression is recommended during concomitant use, especially in patients with renal impairment and/or with elevated magnesium levels. It may be advisable to avoid administration of aminoglycosides in infants with hypermagnesemia', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
