INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1344792, 'Amiloride', 'Quinapril', 'Major', 'Concomitant use of angiotensin converting enzyme (ACE) inhibitors and potassium-sparing diuretics may increase the risk of hyperkalemia. Inhibition of ACE results in decreased aldosterone secretion, which can lead to increases in serum potassium that may be additive with that induced by potassium-sparing diuretics. ACE inhibitors may also cause deterioration of renal function in patients with chronic heart failure, and the risk is increased if they are sodium-depleted or dehydrated after excessive diuresis.', 'DDInter', 1767317475, 'Caution is advised if ACE inhibitors are used with potassium-sparing diuretics, particularly in patients with renal impairment, diabetes, old age, worsening heart failure, and/or a risk for dehydration. Serum potassium and renal function should be checked regularly, and potassium supplementation should generally be avoided unless it is closely monitored. Patients should be given dietary counseling and advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as weakness, listlessness, confusion, tingling of the extremities, and irregular heartbeat.', 'Synergism', 'Caution is advised if ACE inhibitors are used with potassium-sparing diuretics, particularly in patients with renal impairment, diabetes, old age, worsening heart failure, and/or a risk for dehydration.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1344793, 'Amiloride', 'Quinidine', 'Moderate', 'Limited data indicate that amiloride may significantly exaggerate QRS segment prolongation associated with quinidine. In vitro data suggest that the proarrhythmic effect of the combination may be associated with a synergistic increase in sodium channel blockage.', 'DDInter', 1767317475, 'Alternative diuretic therapy, such as a thiazide, is recommended. If amiloride and quinidine must be used together, frequent and careful monitoring of the QRS segment length is recommended.', 'Synergism', 'Alternative diuretic therapy, such as a thiazide, is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
