INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1344844, 'Anistreplase', 'Aminocaproic acid', 'Moderate', 'Coadministration of an antifibrinolytic agent (e.g., aminocaproic acid, tranexamic acid, aprotinin) in combination with a tissue plasminogen activator (t-PA) may reduce the therapeutic effects of both drugs due to opposing pharmacodynamic actions. Aminocaproic acid and tranexamic acid are competitive inhibitors of plasminogen activation. At high concentrations they can also directly inhibit plasmin, which is needed for the degradation of fibrin and thrombolysis. Aprotinin is a broad-spectrum protease inhibitor that inhibits plasmin. It is known to have antifibrinolytic activity and may interact with t-PAs in a similar fashion.', 'DDInter', 1767317475, 'No specific intervention is warranted, but clinicians should be alert to the potential for diminished therapeutic efficacy if a tissue plasminogen activator is administered to a patient who has been treated with an antifibrinolytic agent, and vice versa.', 'Antagonism', 'No specific intervention is warranted, but clinicians should be alert to the potential for diminished therapeutic efficacy if a tissue plasminogen activator is administered to a patient who has been treated with an antifibrinolytic agent, and vice versa.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1344845, 'Aminocaproic acid', 'Anti-inhibitor coagulant complex', 'Major', 'Due to additive or synergistic pharmacodynamic effects, the risk of thrombosis may be increased when antifibrinolytic agents (e.g., aminocaproic acid, aprotinin, tranexamic acid) are coadministered with other hemostatic agents such as anti-inhibitor coagulant complex, factor IX complex, or prothrombin complex.', 'DDInter', 1767317475, 'Concomitant administration of antifibrinolytic agents with other hemostatic agents should generally be avoided. Consult the manufacturer''s product labeling for recommended dosing intervals between products.', 'Synergism', 'Concomitant administration of antifibrinolytic agents with other hemostatic agents should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1344846, 'Aminocaproic acid', 'Carfilzomib', 'Major', 'Venous thromboembolic events, including deep vein thrombosis (DVT) and pulmonary embolism (PE), have occurred during treatment with carfilzomib, and concurrent use of other agents also associated with this adverse effect can potentiate the risk.', 'DDInter', 1767317475, 'Caution is advised when carfilzomib is used with other drugs that can increase the risk of thrombosis. Thromboprophylaxis should be considered according to local treatment protocols. Close monitoring for thromboembolic events is recommended in patients with known risk factors (e.g., prior thrombosis, hyperlipidemia, hypertension, smoking) for thromboembolism. Patients should be advised to seek immediate medical attention if they develop symptoms of thromboembolism, such as shortness of breath, chest pain, hemoptysis, or arm or leg swelling or pain.', 'Synergism', 'Caution is advised when carfilzomib is used with other drugs that can increase the risk of thrombosis.', NULL, NULL, 'Major', 'pharmacodynamic');
