INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35008, 'Crizotinib', 'Atenolol', 'Moderate', 'Crizotinib can cause bradycardia and may have additive effects with other drugs that also slow the heart rate.', NULL, 'Concomitant use of crizotinib with other agents known to cause bradycardia such as beta-blockers, calcium channel blockers, cholinesterase inhibitors, clonidine, and digoxin should generally be avoided whenever possible.', NULL, 'Concomitant use of crizotinib with other agents known to cause bradycardia such as beta-blockers, calcium channel blockers, cholinesterase inhibitors, clonidine, and digoxin should generally be avoided whenever possible. Otherwise, heart rate and blood pressure should be monitored closely. If Grade 2 or 3 symptomatic bradycardia occurs, crizotinib should be withheld, the use of concomitant medications re-evaluated, and the dosage of crizotinib adjusted accordingly. Crizotinib should be permanently discontinued for Grade 4 bradycardia. In patients who develop Grade 4 bradycardia associated with concomitant medications known to cause bradycardia or hypotension, crizotinib should be withheld until Grade 1 or less, and if concomitant medications can be discontinued, crizotinib may be restarted at 250 mg once daily with frequent monitoring.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35063/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] "Product Information. Xalkori (crizotinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[