INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35014, 'Dexmedetomidine', 'Atenolol', 'Moderate', 'Dexmedetomidine has been associated with hypotensive and negative chronotropic effects. Coadministration with other agents causing these effects, especially vasodilators and alpha-2 agonists, may result in additive effects on blood pressure and heart rate.', NULL, 'Caution and monitoring of the patient''s hemodynamic status are recommended during coadministration.', NULL, 'Caution and monitoring of the patient''s hemodynamic status are recommended during coadministration.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35069/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[3] "Product Information. Precedex (dexmedetomidine)" Abbott Pharmaceutical, Abbott Park, IL.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[7] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[8] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[9] "Product Information. Rexulti (brexpiprazole)." Otsuka