INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35162, 'Atenolol', 'Metipranolol (ophthalmic)', 'Moderate', 'Ophthalmic beta blocker solutions are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels. Additive effects of beta blockade may occur in patients receiving both oral and ophthalmic beta blocking agents.', NULL, 'Blood pressure and intraocular pressure monitoring is recommended during concurrent use.', NULL, 'Blood pressure and intraocular pressure monitoring is recommended during concurrent use. Patients should be advised to notify their physician if they experience a slow pulse, irregular heartbeat, dizziness, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35218/', '[1] "Product Information. Timoptic (timolol ophthalmic)." Merck & Co, Inc, West Point, PA.[2] "Product Information. Acupress (carteolol ophthalmic)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35163, 'Atenolol', 'Metocurine', 'Moderate', 'Neuromuscular blocking agents and beta-blockers may have additive cardiovascular depressant effects. Limited data suggest that beta-blockers may modestly potentiate the pharmacologic effects of neuromuscular blocking agents. The exact mechanism of interaction is unknown but may involve effects in the postsynaptic membrane, as beta-blockers alone have been reported to exacerbate or unmask myasthenia gravis.', NULL, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers.', NULL, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers. Respiratory and cardiovascular status should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35219/', '[1] Loan PB, Connolly FM, Mirakhur RK, Kumar N, Farling P "Neuromuscular effects of rocuronium in patients receiving beta-adrenoreceptor blocking, calcium entry blocking and anticonvulsant drugs." Br J Anaesth 78 (1997): 90-1[2] Harrah MD, Way WL, Katzung BG "The interaction of d-tubocurarine with antiarrhythmic drugs." Anesthesiology 33 (1970): 406-10[3] Rozen MS, Whan FM "Prolonged curarization associated with propranolol." Med J Aust 1 (1972): 467-8[4] Murthy VS, Patel KD, Elangovan RG, Hwang TF, Solochek SM, Steck JD, Laddu AR "Cardiovascular and neuromuscular effects of esmolol during induction of anesthesia." J Clin Pharmacol 26 (1986): 351-7[5] Glynne GL "Drug interaction?" Anaesthesia 39 (1984): 293[6] Herishanu Y, Rosenberg P "Beta-blockers and myasthenia gravis." Ann Intern Med 83 (1975): 834-5[7] Yate B, Mostafa SM "Drug interaction?" Anaesthesia 39 (1984): 728-9[8] Chapple DJ, Clark JS, Hughes R "Interaction between atracurium and drugs used in anaesthesia." Br J Anaesth 55 Suppl 1 (1983): s17-22', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35164, 'Atenolol', 'Metolazone', 'Moderate', 'Although they are often combined in clinical practice, diuretics and beta-blockers may increase the risk of hyperglycemia and hypertriglyceridemia in some patients, especially in patients with diabetes or latent diabetes. In addition, the risk of QT interval prolongation and arrhythmias (e.g. torsades de pointes) due to sotalol may be increased by potassium-depleting diuretics.', NULL, 'Monitoring of serum potassium levels, blood pressure, and blood glucose is recommended during coadministration.', NULL, 'Monitoring of serum potassium levels, blood pressure, and blood glucose is recommended during coadministration. Patients should be advised to seek medical assistance if they experience dizziness, weakness, fainting, fast or irregular heartbeats, or loss of blood glucose control.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35220/', '[1] Dean S, Kendall MJ, Potter S, Thompson MH, Jackson DA "Nadolol in combination with indapamide and xipamide in resistant hypertensives." Eur J Clin Pharmacol 28 (1985): 29-33[2] Dornhorst A, Powell SH, Pensky J "Aggravation by propranolol of hyperglycaemic effect of hydrochlorothiazide in type II diabetics without alteration of insulin secretion." Lancet 1 (1985): 123-6[3] "Product Information. Lozol (indapamide)." Rhone-Poulenc Rorer, Collegeville, PA.[4] Marcy TR, Ripley TL "Aldosterone antagonists in the treatment of heart failure." Am J Health Syst Pharm 63 (2006): 49-58[5] Roux A, Le Liboux A, Delhotal B, Gaillot J, Flouvat B "Pharmacokinetics in man of acebutolol and hydrochlorothiazide as single agents and in combination." Eur J Clin Pharmacol 24 (1983): 801-6', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35165, 'Atenolol', 'Midodrine', 'Moderate', 'Midodrine, an alpha-1 adrenergic agent may lead to bradycardia if administered concomitantly with any agent that directly or indirectly reduces heart rate. This may be most important for beta blockers and tricyclic antidepressants.', NULL, 'Patients should be advised to discontinue midodrine if they experience signs or symptoms of decreased heart rate (i.', NULL, 'Patients should be advised to discontinue midodrine if they experience signs or symptoms of decreased heart rate (i.e., slow pulse, dizziness, syncope, cardiac awareness) and to contact their provider for reevaluation.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35221/', '[1] "Product Information. ProAmatine (midodrine)." Roberts Pharmaceutical Corporation, Eatontown, NJ.', 1767363021);
