INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35320, 'Chlorthalidone', 'Levacetylmethadol', 'Major', 'Levomethadyl acetate can cause QT interval prolongation and torsades de pointes. Coadministration with agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in an elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes.', NULL, 'Caution is advised if levomethadyl acetate must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', NULL, 'Caution is advised if levomethadyl acetate must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with levomethadyl acetate. A 12-lead ECG should be performed before initiating therapy, 12 to 14 days after initiating therapy, and periodically thereafter. Patients should be advised to immediately seek medical attention if they experience palpitations, dizziness, lightheadedness, fainting, or seizures.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35376/', '[1] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.[2] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[3] Cohen J "Long-term efficacy and safety of terazosin alone and in combination with other antihypertensive agents." Am Heart J 122 (1991): 919-25[4] Pool JL "Combination antihypertensive therapy with terazosin and other antihypertensive agents: results of clinical trials." Am Heart J 122 (1991): 926-31[5] Achari R, Laddu A "Terazosin: a new alpha adrenoceptor blocking drug." J Clin Pharmacol 32 (1992): 520-3[6] Kuokkanen K, Mattila MJ "Demonstration of an additive antihypertensive effect of prazosin and polythiazide in out-patient." Curr Ther Res Clin Exp 17 (1975): 431-6[7] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35321, 'Chlorthalidone', 'Lithium carbonate', 'Major', 'Thiazide diuretics may cause a rapid increase in serum lithium levels and potentiate the risk of lithium toxicity. The exact mechanism is unknown but may be related to the sodium loss induced by thiazide diuresis, which produces a compensatory increase in proximal tubular reabsorption of sodium along with lithium.', NULL, 'Thiazide diuretics should generally not be prescribed to patients receiving lithium unless close monitoring of serum lithium levels and electrolytes can be rendered.', NULL, 'Thiazide diuretics should generally not be prescribed to patients receiving lithium unless close monitoring of serum lithium levels and electrolytes can be rendered. Lithium dose reductions may be required. Patients should be advised to notify their physician if they experience potential signs and symptoms of lithium toxicity such as drowsiness, dizziness, muscle weakness, vomiting, diarrhea, thirst, polyuria, tinnitus, tremor, ataxia, and blurred vision.', 'Excretion', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35377/', '[1] Crabtree BL, Mack JE, Johnson CD, Amyx BC "Comparison of the effects of hydrochlorothiazide and furosemide on lithium disposition." Am J Psychiatry 148 (1991): 1060-3[2] Hanna ME, Lobao CB, Stewart JT "Severe lithium toxicity associated with indapamide therapy." J Clin Psychopharmacol 10 (1990): 379-80[3] Finley PR, Warner MD, Peabody CA "Clinical relevance of drug interactions with lithium." Clin Pharmacokinet 29 (1995): 172-91[4] Macfie AC "Lithium poisoning precipitated by diuretics." Br Med J 1 (1975): 516[5] Himmelhoch JM, Pou