INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1345066, 'Amiodarone', 'Cariprazine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of cariprazine and its major active metabolite, didesmethyl cariprazine (DDCAR), both of which are primarily metabolized by the isoenzyme. The extent to which other, less potent inhibitors of CYP450 3A4 may interact with cariprazine and its metabolites is unknown.', 'DDInter', 1767317475, 'Caution is advised when cariprazine is prescribed with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as extrapyramidal symptoms, cognitive and motor impairment, hyperglycemia, dyslipidemia, weight gain, orthostatic hypotension, leucopenia, neutropenia, seizures and dysphagia, and the dosage of cariprazine adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when cariprazine is prescribed with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1345067, 'Amiodarone', 'Carteolol', 'Moderate', 'Additive effects of severe bradycardia, cardiac arrest, and ventricular fibrillation may occur in patients administered amiodarone and beta blockers. The mechanism may be related to additive slowing in AV conduction.', 'DDInter', 1767317475, 'Clinical monitoring of patient hemodynamic status and response is recommended.', 'Synergism', 'Clinical monitoring of patient hemodynamic status and response is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
