INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1345119, 'Amiodarone', 'Delavirdine', 'Moderate', 'Concomitant administration may increase plasma concentrations of antiarrhythmic agents. The mechanism is inhibition of CYP450 3A4 and/or 2D6 metabolism by delavirdine. Increased levels of bepridil may be associated with life-threatening cardiac arrhythmias.', 'DDInter', 1767317475, 'Therapeutic concentration monitoring, if available, and close clinical monitoring of patient response and tolerance is recommended.', 'Metabolism', 'Therapeutic concentration monitoring, if available, and close clinical monitoring of patient response and tolerance is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1345120, 'Amiodarone', 'Desflurane', 'Moderate', 'Amiodarone may increase the effect of general anesthetics due to its adrenergic blocking effects. Data have been conflicting; however, the potential for increased risk of intraoperative complications such as arrhythmia, heart block, or pacemaker dependency should be considered.', 'DDInter', 1767317475, 'Cardiorespiratory status should be closely monitored during surgery and until the effects of anesthesia have worn off.', 'Others', 'Cardiorespiratory status should be closely monitored during surgery and until the effects of anesthesia have worn off.', NULL, NULL, 'Moderate', 'pharmacodynamic');
