INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1345133, 'Amiodarone', 'Dihydroergotamine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ergot derivatives, which are primarily metabolized by the isoenzyme. The interaction has occurred in patients receiving ergotamine or dihydroergotamine with potent CYP450 3A4 inhibitors such as macrolide antibiotics and protease inhibitors.', 'DDInter', 1767317475, 'Caution is advised during concomitant use of ergot derivatives with mild or moderate CYP450 3A4 inhibitors. Patients should be monitored for potential ergot toxicity including peripheral vasospasm, ischemia, thrombosis, tachycardia, and hypertension.', 'Metabolism', 'Caution is advised during concomitant use of ergot derivatives with mild or moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1345134, 'Amiodarone', 'Diltiazem', 'Major', 'Amiodarone and diltiazem or verapamil may have additive pharmacodynamic effects when administered concurrently. Sinus arrest, reduced myocardial contractility, and hypotension could result.', 'DDInter', 1767317475, 'The patient''s hemodynamic and electrocardiographic status should be closely monitored, especially during amiodarone loading.', 'Synergism', 'The patient''s hemodynamic and electrocardiographic status should be closely monitored, especially during amiodarone loading.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1345135, 'Amiodarone', 'Dimenhydrinate', 'Moderate', 'The use of higher than recommended dosages of diphenhydramine (e.g., through abuse or misuse) has been associated with serious and potentially fatal cardiac adverse events, including cardiac arrest, and arrhythmia related to prolongation of the QT interval. Coadministration with class Ia (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, dofetilide, sotalol) antiarrhythmic agents may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317475, 'Australian authorities recommend avoiding concomitant use of diphenhydramine and class Ia or class III antiarrhythmic agents. Additionally, patients should be counseled to not exceed the recommended dosage and frequency or duration of use of diphenhydramine, and to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Australian authorities recommend avoiding concomitant use of diphenhydramine and class Ia or class III antiarrhythmic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
