INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1345257, 'Isoprenaline', 'Amiodarone', 'Major', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317475, 'Caution is advised if beta-2 adrenergic agonists are used in combination with other drugs that prolong the QT interval, including class IA and III antiarrhythmic agents, certain neuroleptic agents, phenothiazines, tricyclic antidepressants, quinolones, ketolide and macrolide antibiotics, and cisapride. It may be appropriate to monitor ECG and serum electrolytes during chronic systemic use or high-dose therapy. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Synergism', 'Caution is advised if beta-2 adrenergic agonists are used in combination with other drugs that prolong the QT interval, including class IA and III antiarrhythmic agents, certain neuroleptic agents, phenothiazines, tricyclic antidepressants, quinolones, ketolide and macrolide antibiotics, and cisapride.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1345258, 'Itraconazole', 'Amiodarone', 'Major', 'Coadministration with azole antifungal agents may significantly increase the plasma concentrations of amiodarone. The proposed mechanism is azole inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of amiodarone. The use of amiodarone has been associated with prolongation of the QT interval, thus elevated plasma levels of the drug may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death. Within the azole class, ketoconazole and itraconazole are considered potent inhibitors, while fluconazole is comparatively weak and generally causes clinically significant interactions with CYP450 3A4 substrates only at dosages of 200 mg/day or more.', 'DDInter', 1767317475, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of amiodarone, concomitant use with azole antifungal agents should generally be avoided if possible. Otherwise, caution and close clinical monitoring are recommended, and patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of amiodarone, concomitant use with azole antifungal agents should generally be avoided if possible.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1345259, 'Ivabradine', 'Amiodarone', 'Major', 'Due to its bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased when ivabradine is used with drugs that prolong the QT interval.', 'DDInter', 1767317475, 'Coadministration of ivabradine with drugs that can prolong the QT interval should generally be avoided. Caution and cardiac monitoring are recommended if concomitant use is required.', 'Synergism', 'Coadministration of ivabradine with drugs that can prolong the QT interval should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1345260, 'Ivacaftor', 'Amiodarone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ivacaftor, which is primarily metabolized by the isoenzyme. The interaction occurs to a greater extent when ivacaftor is administered as monotherapy than when administered in combination with lumacaftor, a potent CYP450 3A4 inducer.', 'DDInter', 1767317475, 'Caution is advised if ivacaftor is used with CYP450 3A4 inhibitors. A dosage adjustment for ivacaftor may be required if undue adverse effects occur.', 'Metabolism', 'Caution is advised if ivacaftor is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1345261, 'Amiodarone', 'Ivosidenib', 'Major', 'Class IA (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, dofetilide, sotalol) antiarrhythmic agents can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317475, 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks. Caution and clinical monitoring are recommended if concomitant use is required. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1345262, 'Amiodarone', 'Ixabepilone', 'Moderate', 'The risk of peripheral neuropathy may be increased during concurrent use of two or more agents that are associated with this adverse effect. Patient risk factors include diabetes and age older than 60 years.', 'DDInter', 1767317475, 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet. Since the development of peripheral neuropathy may be dose-related for many drugs, the recommended dosages should generally not be exceeded. Consideration should be given to dosage reduction or immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage.', 'Synergism', 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.', NULL, NULL, 'Moderate', 'pharmacodynamic');
