INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35510, 'Atomoxetine', 'Levosalbutamol', 'Moderate', 'Central nervous system (CNS) stimulants, particularly the amphetamines, can potentiate the adrenergic response to vasopressors and other sympathomimetic agents. Additive increases in blood pressure and heart rate may occur due to enhanced peripheral sympathetic activity.', NULL, 'Caution is advised if CNS stimulants are used with vasopressors or other sympathomimetic agents.', NULL, 'Caution is advised if CNS stimulants are used with vasopressors or other sympathomimetic agents. Pulse and blood pressure should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35566/', '[1] "Product Information. Strattera (atomoxetine)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Focalin (dexmethylphenidate)." Mikart Inc, Atlanta, GA.[3] Cavanaugh JH, Griffith JD, Oates JA "Effect of amphetamine on the pressor response to tyramine: formation of p-hydroxynorephedrine from amphetamine in man." Clin Pharmacol Ther 11 (1970): 656[4] "Product Information. Tenuate (diethylpropion)" Aventis Pharmaceuticals, Swiftwater, PA.[5] "Product Information. Adderall (amphetamine-dextroamphetamine)" Shire Richwood Pharmaceutical Company, Florence, KY.[6] "Product Information. Sanorex (mazindol)" Novartis Pharmaceuticals, East Hanover, NJ.[7] Rosenblatt JE, Lake CR, van Kammen DP, Ziegler MG, Bunney WE Jr "Interactions of amphetamine, pimozide, and lithium on plasma norepineophrine and dopamine-beta-hydroxylase in schizophrenic patients." Psychiatry Res 1 (1979): 45-52[8] Mendelson J, Jones RT, Upton R, Jacob P 3rd "Methamphet