INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35603, 'Ritonavir', 'Atomoxetine', 'Moderate', 'Coadministration with drugs that are inhibitors of CYP450 2D6 may increase the plasma concentrations of atomoxetine, which is primarily metabolized by the isoenzyme.', NULL, 'Pharmacologic response to atomoxetine should be monitored more closely whenever a CYP450 2D6 inhibitor is added to or withdrawn from therapy, as dosage adjustment of atomoxetine may be necessary in extensive metabolizers.', NULL, 'Pharmacologic response to atomoxetine should be monitored more closely whenever a CYP450 2D6 inhibitor is added to or withdrawn from therapy, as dosage adjustment of atomoxetine may be necessary in extensive metabolizers. During coadministration, patients should be advised to contact their physician if they experience excessive adverse effects of atomoxetine such as dizziness, dry mouth, anorexia, sleep disturbances, and palpitations.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35659/', '[1] "Product Information. Strattera (atomoxetine)." Lilly, Eli and Company, Indianapolis, IN.[2] Belle DJ, Ernest CS, Sauer JM, Smith BP, Thomasson HR, Witcher JW "Effect of potent CYP2D6 inhibition by paroxetine on atomoxetine pharmacokinetics." J Clin Pharmacol 42 (2002): 1219-27[3] "Product Information. Ticlid (ticlopidine)." Syntex Laboratories Inc, Palo Alto, CA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35604, 'Rivastigmine', 'Atomoxetine', 'Moderate', 'Acetylcholinesterase inhibitors may have vagotonic effects on the sinoatrial and atrioventricular nodes, which occasionally manifest as bradycardia or heart block (<2%). Because bradycardia is a risk factor for torsade de pointes, a theoretical risk exists when combined with agents that prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35660/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35605, 'Rolapitant', 'Atomoxetine', 'Moderate', 'Coadministration with rolapitant may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. Rolapitant is a moderate CYP450 2D6 inhibitor, with inhibitory effect lasting at least 7 days after a single dose.', NULL, 'Caution is advised when rolapitant is prescribed with drugs that are significantly metabolized by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when rolapitant is prescribed with drugs that are significantly metabolized by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever rolapitant is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35661/', '[1] "Product Information. Varubi (rolapitant)." Tesaro Inc., Waltham, MA.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365204);
