INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35702, 'Darolutamide', 'Atorvastatin', 'Moderate', 'Coadministration with darolutamide may increase the plasma concentrations of drugs that are substrates of the breast cancer resistance protein (BCRP) transporter. The proposed mechanism is decreased clearance due to inhibition of BCRP-mediated intestinal and hepatobiliary efflux by darolutamide.', NULL, 'Concomitant use of darolutamide with drugs that are BCRP substrates should be avoided when possible.', NULL, 'Concomitant use of darolutamide with drugs that are BCRP substrates should be avoided when possible. If coadministration is required, patients should be monitored carefully for increased adverse reactions of these drugs and dosing adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35758/', '[1] "Product Information. Nubeqa (darolutamide)." Bayer HealthCare Pharmaceuticals Inc., Whippany, NJ.[2] "Product Information. Venclexta (venetoclax)." AbbVie US LLC, North Chicago, IL.[3] "Product Information. Nubeqa (darolutamide)." Bayer HealthCare Pharmaceuticals Inc., Whippany, NJ.', 1767365204);
