INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35802, 'Atorvastatin', 'Mitotane', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of atorvastatin and its active metabolites, all of which are substrates of the isoenzyme.', NULL, 'The potential for diminished pharmacologic effects of atorvastatin should be considered during coadministration with CYP450 3A4 inducers.', NULL, 'The potential for diminished pharmacologic effects of atorvastatin should be considered during coadministration with CYP450 3A4 inducers. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. Otherwise, pharmacologic response to atorvastatin should be closely monitored, and the dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35858/', '[1] Murphy MJ, Dominiczak MH "Efficacy of statin therapy: possible effect of phenytoin." Postgrad Med J 75 (1999): 359-60[2] Bullman J, Nicholls A, Van Landingham K, et al. "Effects of lamotrigine and phenytoin on the pharmacokinetics of atorvastatin in healthy volunteers." Epilepsia 52 (2011): 1351-8[3] "Product Information. Lipitor (atorvastatin)." Parke-Davis, Morris Plains, NJ.[4] Khandwala HM "Lipid lowering inefficacy of high-dose statin therapy due to concurrent use of phenytoin." South Med J 99 (2006): 1385-7[5] Gerber JG, Rosenkranz SL, Fichtenbaum CJ, et al. "Effect of efavirenz on the pharmacokinetics of simvastatin, atorvastatin, and pravastatin: results of AIDS Clinical Trials Group 5108 Study." J Acquir Immune Defic Syndr 39 (2005): 307-12[6] Richter WO, Jacob BG, Schwandt P "Interaction between fibre and lovastatin." Lancet 338 (1991): 706[7] "Product Information. Lipitor (atorvastatin)." Parke-Davis, Morris Plains, NJ.[8] Boberg M, Angerbauer R, Fey P, Kanhai WK, Karl W, Kern A, Ploschke J, Radtke M "Metabolism of cerivastatin by human liver microsomes in vitro. Characterization of primary metabolic pathways and of cytochrome P45 isozymes involved." Drug Metab Dispos 25 (1997): 321-31[9] McMillan K "Considerations in the formulary selection of hydroxymethylglutaryl coenzyme a reductase inhibitors." Am J Health Syst Pharm 53 (1996): 2206-14[10] Bailey DG, Malcolm J, Arnold O, Spence JD "Grapefruit juice-drug interactions." Br J Clin Pharmacol 46 (1998): 101-10[11] Neuvonen PJ, Backman JT, Niemi M "Pharmacokinetic comparison of the potential over-the-counter statins simvastatin, lovastatin, fluvastatin and pravastatin." Clin Pharmacokinet 47 (2008): 463-74[12] Lilja JJ, Kivisto KT, Neuvonen PJ "Grapefruit juice increases serum concentrations of atorvastatin and has no effect on pravastatin." Clin Pharmacol Ther 66 (1999): 118-27', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35803, 'Atorvastatin', 'Mitoxantrone', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of mitoxantrone, which is a substrate of the efflux transporter also known as ABCB1 or MDR1. The interaction has been studied with cyclosporine, a potent P-gp inhibitor.', NULL, 'Caution is advised if mitoxantrone is prescribed in combination with a P-gp inhibitor.', NULL, 'Caution is advised if mitoxantrone is prescribed in combination with a P-gp inhibitor. Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35859/', '[1] "Product Information. Novantrone (mitoxantrone)." Immunex Corporation, Seattle, WA.[2] "Multum Information Services, Inc. Expert Review Panel"[3] "Product Information. Novantrone (mitoxantrone)." Immunex Corporation, Seattle, WA.[4] "Multum Information Services, Inc. Expert Review Panel"[5] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35804, 'Atorvastatin', 'Modafinil', 'Minor', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. Modafinil and armodafinil are modest inducers of CYP450 3A4, and pharmacokinetic studies suggest that their effects may be primarily intestinal rather than hepatic. Thus, clinically significant interactions would most likely be expected with drugs that have low oral bioavailability due to significant intestinal CYP450 3A4-mediated first-pass metabolism (e.g., buspirone, cyclosporine, lovastatin, midazolam, saquinavir, simvastatin, sirolimus, tacrolimus, triazolam, calcium channel blockers). However, the potential for interaction should be considered with any drug metabolized by CYP450 3A4, especially given the high degree of interpatient variability with respect to CYP450-mediated metabolism. Pharmacologic response to these drugs may be altered and should be monitored more closely whenever modafinil or armodafinil is added to or withdrawn from therapy. Dosage adjustments may be required if an interaction is suspected.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35860/', '[1] Robertson P Jr, Hellriegel ET, Arora S, Nelson M "Effect of modafinil on the pharmacokinetics of ethinyl estradiol and triazolam in healthy volunteers." Clin Pharmacol Ther 71 (2002): 46-56[2] Doherty MM, Charman WN "The mucosa of the small intestine: how clinically relevant as an organ of drug metabolism?" Clin Pharmacokinet 41 (2002): 235-53[3] "Product Information. Pro