INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35839, 'Remdesivir', 'Atorvastatin', 'Moderate', 'Concomitant use of remdesivir with other agents that are known to induce hepatotoxicity may theoretically increase the risk of liver injury. Data from investigational use and clinical studies suggest that remdesivir may be associated with hepatotoxic effects.', NULL, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible.', NULL, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible. Liver function should be evaluated prior to starting remdesivir and monitored during treatment as clinically appropriate. Remdesivir should not be initiated in patients with ALT greater than or equal to 5 times the upper limit of normal (ULN) at baseline. Likewise, remdesivir should be discontinued immediately if ALT increases to 5 times ULN or greater during treatment (may be restarted when ALT falls below 5 times ULN), or if ALT elevation is accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35895/', '[1] Gilead Sciences, Inc "About Remdesivir. Available from: URL: https://www.gilead.com/purpose/advancing-global-health/covid-19/about-remdesivir." ([2020]):[2] US Food and Drug Administration "Fact sheet for health care providers emergency use authorization (EUA) of remdesivir (GS-5734TM) Available from: URL: https://www.fda.gov/media/137566/download." ([2020, May 1]]):[3] European Medicines Agency "Summary on compassionate use. Remdesivir Gilead. Available from: URL: https://www.ema.europa.eu/en/documents/other/summary-compassionate-use-remdesivir-gilead_en.pdf." ([2020 Apr 03]):[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35840, 'Ribociclib', 'Atorvastatin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of HMG-CoA reductase inhibitors (i.e., statins) that are metabolized by the isoenzyme. Lovastatin and simvastatin are particularly susceptible because of their low oral bioavailability, but others such as atorvastatin and cerivastatin may also be affected.', NULL, 'Caution is recommended if atorvastatin, cerivastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) is prescribed with a CYP450 3A4 inhibitor.', NULL, 'Caution is recommended if atorvastatin, cerivastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) is prescribed with a CYP450 3A4 inhibitor. It is advisable to monitor lipid levels and use the lowest effective statin dose. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed. Fluvastatin, pravastatin, and rosuvastatin are not expected to interact with CYP450 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35896/', '[1] Yeo KR, Yeo WW, Wallis EJ, Ramsay LE "Enhanced cholesterol reduction by simvastatin in diltiazem-treated patients." Br J Clin Pharmacol 48 (1999): 610-5[2] Andreou ER, Ledger S "Potential drug interaction between simvastatin and danazol causing rhabdomyolysis