INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35864, 'Sulfinpyrazone', 'Atorvastatin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of atorvastatin and its active metabolites, all of which are substrates of the isoenzyme.', NULL, 'The potential for diminished pharmacologic effects of atorvastatin should be considered during coadministration with CYP450 3A4 inducers.', NULL, 'The potential for diminished pharmacologic effects of atorvastatin should be considered during coadministration with CYP450 3A4 inducers. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. Otherwise, pharmacologic response to atorvastatin should be closely monitored, and the dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35920/', '[1] Murphy MJ, Dominiczak MH "Efficacy of statin therapy: possible effect of phenytoin." Postgrad Med J 75 (1999): 359-60[2] Bullman J, Nicholls A, Van Landingham K, et al. "Effects of lamotrigine and phenytoin on the pharmacokinetics of atorvastatin in healthy volunteers." Epilepsia 52 (2011): 1351-8[3] "Product Information. Lipitor (atorvastatin)." Parke-Davis, Morris Plains, NJ.[4] Khandwala HM "Lipid lowering inefficacy of high-dose statin therapy due to concurrent use of phenytoin." South Med J 99 (2006): 1385-7[5] Gerber JG, Rosenkranz SL, Fichtenbaum CJ, et al. "Effect of efavirenz on the pharmacokinetics of simvastatin, atorvastatin, and pravastatin: results of AIDS Clinical Trials Group 5108 Study." J Acquir Immune Defic Syndr 39 (2005): 307-12[6] Richter WO, Jacob BG, Schwandt P "Interacti