INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35918, 'Ritonavir', 'Atovaquone', 'Moderate', 'Coadministration with ritonavir may decrease the plasma concentrations of atovaquone and proguanil. The mechanism of interaction has not been established, but may involve ritonavir induction of the glucuronidation of atovaquone and the CYP450 2C19-mediated metabolism of proguanil.', NULL, 'The potential for treatment failure should be considered when atovaquone or atovaquone/proguanil is used with ritonavir.', NULL, 'The potential for treatment failure should be considered when atovaquone or atovaquone/proguanil is used with ritonavir. Some experts recommend avoiding concomitant use. If coadministration is required, an increased dosage of atovaquone and proguanil may be required.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35974/', '[1] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[2] Van Luin M, Van der Ende ME, Richter C, et al. "Lower atovaquone/proguanil concentrations in patients taking efavirenz, lopinavir/ritonavir or atazanavir/ritonavir." AIDS 24 (2010): 1223-6[3] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[4] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35919, 'Iodide I-123', 'Atovaquone', 'Moderate', 'INTERVAL: Certain drugs may affect the pharmacokinetics, pharmacodynamics, and/or diagnostic results of radioiodides.', NULL, 'Antithyroid agents such as carbimazole or propylthiouracil should generally be withheld for 1 week before administration of sodium iodide I-131 or I-123, and for a few days afterward.', NULL, 'Antithyroid agents such as carbimazole or propylthiouracil should generally be withheld for 1 week before administration of sodium iodide I-131 or I-123, and for a few days afterward. Salicylates, steroids, anticoagulants, antihistamines, antiparasitics, penicillins, sulfonamides, tolbutamide, thiopental, and nitroprusside should generally be withheld for 1 week. Amiodarone, lithium, and benzodiazepines should be withheld for at least 4 weeks.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35975/', '[1] "Product Information. Sodium Iodide I-123 (sodium iodide I-123)." GE Healthcare, Princeton, NJ.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.', 1767365204);
