INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (35928, 'Anisindione', 'Proguanil', 'Moderate', 'Proguanil may potentiate the hypoprothrombinemic effect of warfarin and similar anticoagulants through possible interference with metabolic pathways.', NULL, 'Given the potential for interaction and the high degree of interpatient variability with respect to warfarin metabolism, patients should be closely monitored during concomitant therapy with proguanil.', NULL, 'Given the potential for interaction and the high degree of interpatient variability with respect to warfarin metabolism, patients should be closely monitored during concomitant therapy with proguanil. The INR should be checked frequently and warfarin dosage adjusted accordingly, particularly following initiation or discontinuation of proguanil therapy in patients who are stabilized on their warfarin regimen. The same precaution may be applicable during therapy with other oral anticoagulants, although clinical data are lacking. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35984/', '[1] "Warfarin potentiated by proguanil." BMJ 303 (1991): 789[2] "Product Information. Malarone (atovaquone-proguanil)" Glaxo Wellcome, Research Triangle Pk, NC.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medicat