INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72090, 'Diclofenamide', 'Clozapine', 'Moderate', 'One case report has suggested that combination of carbonic anhydrase inhibitors (CAIs) and clozapine may increase the risk of neutropenia. The case involved methazolamide, and the authors speculate that acetazolamide may participate in this interaction as well.', NULL, 'Close monitoring is advisable during concomitant use.', NULL, 'Close monitoring is advisable during concomitant use. White blood cell (WBC) counts and absolute neutrophil counts (ANC) should be measured regularly according to official recommendations, and clozapine should be discontinued if cell counts fall below critical limits. Patients should be counseled to notify their doctor immediately if they experience influenza-like symptoms, fever, or a sore throat.', 'Synergism', 'Fluphenazine, Haloperidol, Thiothixene, Risperidone, Amisulpride, Quetiapine, Prochlorperazine, Promazine, Thioridazine, Lumateperone, Trifluoperazine, More', 'Tinidazole, Ascorbic acid, Simeprevir, Lactic acid, Mitapivat, Amphotericin B, Ciclopirox, Darunavir, Probenecid, Tioconazole, Brinzolamide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72177/', '[1] "Product Information. Diamox (acetazolamide)." Lederle Laboratories, Wayne, NJ.[2] Burke WJ, Ranno AE "Neutropenia with clozapine and methazolamide." J Clin Psychopharmacol 14 (1994): 357-8[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] Warrington SJ, Ankier SI, Turner P "E