INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72127, 'Enoxacin', 'Clozapine', 'Major', 'Coadministration with certain quinolone antibiotics may increase the plasma concentrations and the risk of adverse effects, including QT prolongation, of clozapine. The mechanism is inhibition of CYP450 1A2, the isoenzyme primarily responsible for the metabolic clearance of clozapine. Quinolones that may inhibit CYP450 1A2 include ciprofloxacin, enoxacin, grepafloxacin, nalidixic acid, norfloxacin, and perfloxacin (not all commercially available).', NULL, 'Pharmacologic response and plasma clozapine concentrations should be monitored more closely whenever certain quinolones such as ciprofloxacin or norfloxacin are added to or withdrawn from therapy, and the clozapine dosage adjusted as necessary.', NULL, 'Pharmacologic response and plasma clozapine concentrations should be monitored more closely whenever certain quinolones such as ciprofloxacin or norfloxacin are added to or withdrawn from therapy, and the clozapine dosage adjusted as necessary.', 'Metabolism', 'Haloperidol, Thiothixene, Risperidone, Amisulpride, Quetiapine, Prochlorperazine, Promazine, Thioridazine, Lumateperone, Trifluoperazine, Cariprazine, More', 'Trovafloxacin, Delafloxacin, Cinoxacin', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72214/', '[1] Markowitz JS, Gill HS, Devane CL, Mintzer JE "Fluoroquinolone inhibition of clozapine metabolism." Am J Psychiatry 154 (1997): 881[2] Kinzig-Schippers M, Fuhr U, Zaigler M, et al "Interaction of pefloxacin and enoxacin with the human cytochrome P450 enzyme CYP1A2." Clin Pharmacol Ther 65 (1999): 262-74[3] Fuhr U, Anders EM, Mahr G, Sorgel F, Staid AH "Inhibi