INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72654, 'Crizotinib', 'Cobicistat', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of crizotinib, which is primarily metabolized by the isoenzyme. Because crizotinib is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', NULL, 'Concomitant use of crizotinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of crizotinib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of crizotinib during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Lenvatinib, Trametinib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Oseltamivir, Acyclovir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72741/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] "Product Information. Xalkori (crizotinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[4] "Product Information. Xalkori (crizotinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72655, 'Cyclosporine', 'Cobicistat', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the blood concentrations of cyclosporine, which is primarily metabolized by the isoenzyme. The risk of nephro- and neurotoxicity associated with cyclosporine may be increased.', NULL, 'Caution is advised if cyclosporine is used with potent inhibitors of CYP450 3A4.', NULL, 'Caution is advised if cyclosporine is used with potent inhibitors of CYP450 3A4. Cyclosporine blood levels and renal function should be checked frequently and the dosage adjusted accordingly, particularly following initiation or discontinuation of macrolide therapy in patients who are stabilized on their cyclosporine regimen. Patients should be advised to notify their doctor if they experience possible signs of cyclosporine toxicity such as nausea, vomiting, diarrhea, abdominal pain, dizziness, fatigue, headache, tremors, and convulsions. Alternative treatment that do not interfere with cyclosporine metabolism should be considered whenever possible.', 'Metabolism', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, More', 'Cabotegravir, Zanamivir, Velpatasvir, Stavudine, Elbasvir, Tecovirimat, Pibrentasvir, Rimantadine, Elvitegravir, Peramivir, Emtricitabine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72742/', '[1] "Product Information. Rescriptor (delavirdine)." Pharmacia and Upjohn, Kalamazoo, MI.[2] Ducharme MP, Warbasse LH, Edwards DJ "Disposition of intravenous and oral cyclosporine after administration with grapefruit juice." Clin Pharmacol Ther 57 (1995): 485-91[3] Min DI, Ku YM, Perry PJ, Ukah FO, Ashton K, Martin MF, Hunsicker LG "Effect of grapefruit juice on cyclosporine pharmacokinetics in renal transplant patients." Transplantation 62 (1996): 123-5[4] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[5] Bailey DG, Arnold JMO, Spence JD "Grapefruit juice and drugs - how significant is the interaction." Clin Pharmacokinet 26 (1994): 91-8[6] "Grapefruit juice interactions with drugs." Med Lett Drugs Ther 37 (1995): 73-4[7] Honcharik N, Yatscoff RW, Jeffery JR, Rush DN "The effect of meal composition on cyclosporine absorption." Transplantation 52 (1991): 1087-9[8] Tsunoda SM, Harris RZ, Christians U, et al. "Red wine decreases cyclosporine bioavailability." Clin Pharmacol Ther 70 (2001): 462-7[9] Yee GC, Stanley DL, Pessa LJ, et al. "Effect of grrapefruit juice on blood cyclosporin concentration." Lancet 345 (1995): 955-6[10] Ioannidesdemos LL, Christophidis N, Ryan P, Angelis P, Liolios L, Mclean AJ "Dosing implications of a clinical interaction between grapefruit juice and cyclosporine and metabolite concentrations in patients with autoimmune diseases." J Rheumatol 24 (1997): 49-54[11] Oliveira-Freitas VL, Dalla Costa T, Manfro RC, Cruz LB, Schwartsmann G "Influence of purple grape juice in cyclosporine availability." J Ren Nutr 20 (2010): 309-13[12] Tan KKC, Trull AK, Uttridge JA, Metcalfe S, Heyes CS, Facey S, Evans DB "Effect of dietary fat on the pharmacokinetics and pharmacodynamics of cyclosporine in kidney transplant recipients." Clin Pharmacol Ther 57 (1995): 425-33[13] Hollander AAMJ, Vanrooij J, Lentjes EGWM, Arbouw F, Vanbree JB, Schoemaker RC, Vanes LA, Vanderwoude FJ, Cohen AF "The effect of grapefruit juice on cyclosporine and prednisone metabolism in transplant patients." Clin Pharmacol Ther 57 (1995): 318-24[14] Ducharme MP, Provenzano R, Dehoornesmith M, Edwards DJ "Trough concentrations of cyclosporine in blood following administration with grapefruit juice." Br J Clin Pharmacol 36 (1993): 457-9', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72656, 'Dabrafenib', 'Cobicistat', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 and/or 2C8 may increase the plasma concentrations of dabrafenib and its active metabolites. Coadministration with dabrafenib may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4, including many of the known inhibitors of the isoenzyme such as conivaptan, delavirdine, nefazodone, telithromycin, and most azole antifungal agents, macrolide antibiotics, and protease inhibitors.', NULL, 'The use of dabrafenib with potent CYP450 2C8 inhibitors such as gemfibrozil or potent CYP450 3A4 inhibitors such as ceritinib, clarithromycin, cobicistat, conivaptan, delavirdine, erythromycin, idelalisib, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents should generally be avoided if possible.', NULL, 'The use of dabrafenib with potent CYP450 2C8 inhibitors such as gemfibrozil or potent CYP450 3A4 inhibitors such as ceritinib, clarithromycin, cobicistat, conivaptan, delavirdine, erythromycin, idelalisib, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents should generally be avoided if possible. Some authorities recommend avoiding concomitant use of dabrafenib during and for 2 weeks after treatment with itraconazole. Otherwise, patients should be closely monitored for development of adverse effects such as febrile reactions (high fever or fever accompanied by rigors, hypotension, dehydration, or renal failure), hyperglycemia, uveitis, and cutaneous malignancies (e.g., squamous cell carcinoma, keratoacanthoma, melanoma).', 'Metabolism', 'Lenvatinib, Trametinib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Dolutegravir, Oseltamivir, Acyclovir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72743/', '[1] "Product Information. Tafinlar (dabrafenib)." GlaxoSmithKline, Research Triangle Park, NC.[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[6] "Product Information. Tafinlar (dabrafenib)." GlaxoSmithKline, Research Triangle Park, NC.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72657, 'Darifenacin', 'Cobicistat', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of darifenacin, which is partially metabolized by the isoenzyme. Darifenacin is also metabolized by CYP450 2D6, but 3A4 is the major pathway of metabolism in so-called poor metabolizers of 2D6.', NULL, 'The dosage of darifenacin should not exceed 7.', NULL, 'The dosage of darifenacin should not exceed 7.5 mg/day when used with potent CYP450 3A4 inhibitors. Some authorities recommend avoiding concomitant use of darifenacin during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Apomorphine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Papaverine, Phenyl salicylate, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72744/', '[1] Niwa T, Shiraga T, Takagi A "Effect of antifungal drugs on cytochrome P450 (CYP) 2C9, CYP2C19, CYP3A4 activities in human liver microsomes." Biol Pharm Bull 28 (2005): 1805-8[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] "Product Information. Enablex (darifenacin)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72658, 'Darolutamide', 'Cobicistat', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 that can also inhibit P-glycoprotein (P-gp) may significantly increase the plasma concentrations of darolutamide, which is a substrate of both the isoenzyme and the efflux transporter.', NULL, 'Caution is advised when darolutamide is used with dual P-gp and potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised when darolutamide is used with dual P-gp and potent CYP450 3A4 inhibitors. Patients should be monitored more frequently for adverse effects such as fatigue, hypertension, neutropenia and abnormal liver function tests, and the darolutamide dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Lamivudine, Tenofovir alafenamide, Maraviroc, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, Stavudine, More', 'Anastrozole, Fulvestrant, Letrozole, Abarelix, Flutamide, Bicalutamide, Degarelix, Nilutamide, Aminoglutethimide, Exemestane', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72745/', '[1] "Product Information. Nubeqa (darolutamide)." Bayer HealthCare Pharmaceuticals Inc., Whippany, NJ.[2] "Product Information. Nubeqa (darolutamide)." Bayer HealthCare Pharmaceuticals Inc., Whippany, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72659, 'Dasatinib', 'Cobicistat', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and pharmacologic effects of dasatinib, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of dasatinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of dasatinib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of dasatinib during and for 2 weeks after treatment with itraconazole. Close monitoring for toxicity (e.g., myelosuppression, bleeding complications, fluid retention, excessive slowing of heart rate or other conduction disturbances) and a reduction of dasatinib dosage to a range of 20 to 40 mg daily should be considered if there are no alternatives and concomitant use with these agents is necessary.', 'Metabolism', 'Lenvatinib, Trametinib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Oseltamivir, Brincidofovir, Stavudine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72746/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] "Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb, Princeton, NJ.[4] "Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb, Princeton, NJ.[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[8] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72660, 'Deflazacort', 'Cobicistat', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of 21-desdeflazacort, the active metabolite of deflazacort that is formed by esterases after oral administration and further metabolized by CYP450 3A4 to several inactive metabolites.', NULL, 'The manufacturer suggests using one-third of the recommended dosage of deflazacort when coadministered with potent or moderate CYP450 3A4 inhibitors.', NULL, 'The manufacturer suggests using one-third of the recommended dosage of deflazacort when coadministered with potent or moderate CYP450 3A4 inhibitors. Some authorities recommend avoiding concomitant use of deflazacort and CYP450 3A inhibitors unless the benefit outweighs the risk, in which case patients should be monitored for systemic corticosteroid side effects.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72747/', '[1] "Product Information. Emflaza (deflazacort)." Marathon Pharmaceuticals, Deerfield, IL.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72661, 'Delavirdine', 'Cobicistat', 'Moderate', 'Cobicistat may increase the plasma concentrations of antiretroviral agents. The plasma concentrations of cobicistat may also be increased or reduced in the presence of antiretroviral agents. The proposed mechanism is cobicistat inhibition of the CYP450 3A4 isoenzyme, of which antiretroviral agents may be substrates, and the inhibition or induction of CYP450 3A4 by concomitant antiretroviral medications.', NULL, 'Cobicistat is not intended for use with more than one antiretroviral medication that requires pharmacokinetic enhancement, such as two protease inhibitors or elvitegravir in combination with a protease inhibitor.', NULL, 'Cobicistat is not intended for use with more than one antiretroviral medication that requires pharmacokinetic enhancement, such as two protease inhibitors or elvitegravir in combination with a protease inhibitor. In addition, cobicistat should not be used concomitantly with ritonavir due to their similar effects on CYP450 3A4. According to some authorities, use of the antiretroviral combinations of atazanavir-cobicistat or darunavir-cobicistat concomitantly with the CYP450 3A4 inducers efavirenz, etravirine, or nevirapine is also not recommended.', 'Metabolism', 'Cabotegravir, Zanamivir, Lenacapavir, Valaciclovir, Telaprevir, Velpatasvir, Oseltamivir, Cobicistat, Acyclovir, Remdesivir, Famciclovir, More', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Telaprevir, Velpatasvir, Oseltamivir, Acyclovir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72748/', '[1] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.[2] "Product Information. Stribild (cobicistat/elvitegravir/emtricitabine/tenofov)." Gilead Sciences, Foster City, CA.[3] "Product Information. Viramune (nevirapine)." Boehringer-Ingelheim, Ridgefield, CT.[4] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb, Princeton, NJ.[5] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals, Wilmington, DE.[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] "Product Information. Prezcobix (cobicistat-darunavir)." Janssen Pharmaceuticals, Titusville, NJ.[8] Cerner Multum, Inc. "Australian Product Information." O 0[9] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[10] "Product Information. Tybost (cobicistat)." Gilead Sciences, Foster City, CA.[11] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[12] Cerner Multum, Inc. "Australian Product Information." O 0[13] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[14] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72662, 'Digoxin', 'Cobicistat', 'Moderate', 'Coadministration with cobicistat may increase serum digoxin concentrations and the risk of digoxin toxicity. The proposed mechanism is reduced clearance of digoxin due to inhibition of P-glycoprotein-mediated renal tubular secretion by cobicistat.', NULL, 'Caution is advised if digoxin must be used in combination with cobicistat.', NULL, 'Caution is advised if digoxin must be used in combination with cobicistat. Pharmacologic response and serum digoxin levels should be monitored more closely whenever cobicistat is added to or withdrawn from therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'Digitoxin', 'Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Cabotegravir, Zanamivir, Rilpivirine, Dolutegravir, Brincidofovir, Remdesivir, Elbasvir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72749/', '[1] "Product Information. Stribild (cobicistat/elvitegravir/emtricitabine/tenofov)." Gilead Sciences, Foster City, CA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Darcy PF "Nutrient-drug interactions." Adverse Drug React Toxicol Rev 14 (1995): 233-54[4] Becquemont L, Verstuyft C, Kerb R, et al. "Effect of grapefruit juice on digoxin pharmacokinetics in humans." Clin Pharmacol Ther 70 (2001): 311-6', 1767369485);
