INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72684, 'Eplerenone', 'Cobicistat', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations and risk of adverse reactions of eplerenone, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of eplerenone with potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, 'Concomitant use of eplerenone with potent CYP450 3A4 inhibitors is considered contraindicated. Some authorities consider concomitant administration of eplerenone and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Amiloride, Triamterene, Spironolactone', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72771/', '[1] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[2] "Product Information. Inspra (eplerenone)." Searle, Chicago, IL.[3] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
