INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72694, 'Felodipine', 'Cobicistat', 'Moderate', 'Coadministration with cobicistat may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 or 2D6 isoenzymes and/or P-glycoprotein (P-gp) efflux transporter.', NULL, 'Caution is advised if cobicistat is used concomitantly with medications that undergo metabolism primarily by CYP450 3A4 and/or 2D6 or are substrates of P-glycoprotein, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if cobicistat is used concomitantly with medications that undergo metabolism primarily by CYP450 3A4 and/or 2D6 or are substrates of P-glycoprotein, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever cobicistat is added to or withdrawn from therapy.', 'Metabolism', 'Acetylsalicylic acid, Bisoprolol, Atenolol, Sotalol, Clevidipine, Celecoxib, Enalapril, Moexipril, Ramipril, Hydrochlorothiazide, Lisinopril, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, Brincidofovir, Stavudine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72781/', '[1] "Product Information. Stribild (cobicistat/elvitegravir/emtricitabine/tenofov)." Gilead Sciences, Foster City, CA.[2] Bailey DG, Dresser GK "Natural products and adverse drug interactions." Can Med Assoc J 170 (2004): 1531-2[3] Ho PC, Ghose K, Saville D, Wanwimolruk S "Effect of grapefruit juice on pharmacokinetics and pharmacodynamics of verapamil enantiomers in healthy volunteers." Eur J Clin 