INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72734, 'Cobicistat', 'Macitentan', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of macitentan, which is primarily metabolized by the isoenzyme. Macitentan was well tolerated with or without ketoconazole in the study, and there were no relevant differences in safety parameters between the treatments.', NULL, 'According to the product labeling, concomitant use of macitentan with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided.', NULL, 'According to the product labeling, concomitant use of macitentan with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided. Alternative treatment options for pulmonary arterial hypertension are recommended when potent CYP450 3A4 inhibitors are needed as part of HIV treatment.', 'Metabolism', 'Metyrosine', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72821/', '[1] "Product Information. Opsumit (macitentan)." Actelion Pharmaceuticals US Inc, South San Francisco, CA.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72735, 'Cobicistat', 'Maraviroc', 'Moderate', 'Cobicistat may increase the plasma concentrations of antiretroviral agents. The plasma concentrations of cobicistat may also be increased or reduced in the presence of antiretroviral agents. The proposed mechanism is cobicistat inhibition of the CYP450 3A4 isoenzyme, of which antiretroviral agents may be substrates, and the inhibition or induction of CYP450 3A4 by concomitant antiretroviral medications.', NULL, 'Cobicistat is not intended for use with more than one antiretroviral medication that requires pharmacokinetic enhancement, such as two protease inhibitors or elvitegravir in combination with a protease inhibitor.', NULL, 'Cobicistat is not intended for use with more than one antiretroviral medication that requires pharmacokinetic enhancement, such as two protease inhibitors or elvitegravir in combination with a protease inhibitor. In addition, cobicistat should not be used concomitantly with ritonavir due to their similar effects on CYP450 3A4. According to some authorities, use of the antiretroviral combinations of atazanavir-cobicistat or darunavir-cobicistat concomitantly with the CYP450 3A4 inducers efavirenz, etravirine, or nevirapine is also not recommended.', 'Metabolism', 'Cabotegravir, Zanamivir, Lenacapavir, Valaciclovir, Telaprevir, Velpatasvir, Oseltamivir, Cobicistat, Acyclovir, Remdesivir, Famciclovir, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Rilpivirine, Dolutegravir, Oseltamivir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72822/', '[1] "Product Informat