INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72743, 'Cobicistat', 'Midostaurin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of midostaurin and its active metabolites, which are all substrates of the isoenzyme. The increase in midostaurin concentrations may be particularly pronounced when CYP450 3A4 inhibitors are administered during the first week of midostaurin administration.', NULL, 'Concomitant use of midostaurin with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of midostaurin with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, patients should be closely monitored for increased adverse reactions (e.g., nausea, vomiting, diarrhea, edema, hyperglycemia, hyperuricemia, QT prolongation, neutropenia, lymphopenia, thrombocytopenia, anemia), especially during the first week of consecutive midostaurin administration in patients with advanced systemic mastocytosis and during the first week of midostaurin administration in each cycle of chemotherapy in patients with acute myeloid leukemia.', 'Metabolism', 'Lenvatinib, Trametinib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, Stavudine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72830/', '[1] Dutreix C, Munarini F, Lorenzo S, Roesel J, Wang Y "Investigation into CYP3A4-mediated drug-drug interactions on midostaurin in healthy volunteers." Cancer Chemother Pharmacol 72 (2013): 1223-34[2] "Product Information. Rydapt (midostaurin)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Rydapt (midostaurin)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72744, 'Cobicistat', 'Mifepristone', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of mifepristone, which is primarily metabolized by the isoenzyme. The clinical impact of this interaction has not been studied.', NULL, 'Due to its potent antiglucocorticoid effects and potential to cause dose-related prolongation of the QT interval, extreme caution is advised when mifepristone is prescribed in combination with strong CYP450 3A4 inhibitors such as, but not limited to, itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, delavirdine, nefazodone, telithromycin, and protease inhibitors.', NULL, 'Due to its potent antiglucocorticoid effects and potential to cause dose-related prolongation of the QT interval, extreme caution is advised when mifepristone is prescribed in combination with strong CYP450 3A4 inhibitors such as, but not limited to, itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, delavirdine, nefazodone, telithromycin, and protease inhibitors. When used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, the dosage of mifepristone should be limited to 300 mg/day and used only when necessary.', 'Metabolism', 'Ulipristal, Danazol, Raloxifene, Bazedoxifene, Prasterone', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Oseltamivir, Acyclovir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72831/', '[1] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[2] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.[3] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[4] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72745, 'Cobicistat', 'Mirabegron', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of mirabegron, which has been shown in vitro to be a substrate of the isoenzyme. However, in vivo results indicate that CYP450 3A4 may play a limited role in the overall elimination. No dosage adjustment is recommended when mirabegron is administered in combination with ketoconazole and probably other CYP450 3A4 inhibitors.', NULL, '-', NULL, '-', 'Metabolism', 'Apomorphine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Papaverine, Phenyl salicylate, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72832/', '[1] "Product Information. Myrbetriq (mirabegron)." Astellas Pharma US, Inc, Deerfield, IL.[2] "Product Information. Myrbetriq (mirabegron)." Astellas Pharma US, Inc, Deerfield, IL.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72746, 'Cobicistat', 'Mirtazapine', 'Moderate', 'Coadministration of mirtazapine with drugs that inhibit one or more of its metabolic pathways may result in increased plasma concentrations of mirtazapine. In vitro data from human liver microsomes indicate that CYP450 2D6 and 1A2 are involved in the formation of the 8-hydroxy metabolite, while CYP450 3A4 is primarily responsible for the formation of the N-desmethyl and N-oxide metabolites.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of mirtazapine should be considered during coadministration of drugs that inhibit CYP450 1A2, 2D6 and/or 3A4.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of mirtazapine should be considered during coadministration of drugs that inhibit CYP450 1A2, 2D6 and/or 3A4. Therapeutic response to mirtazapine should be monitored more closely following initiation, discontinuation, or dosing change of CYP450 inhibitors, and the mirtazapine dosage adjusted as necessary.', 'Metabolism', 'Phenelzine, Tranylcypromine, Desvenlafaxine, Nefazodone, Isocarboxazid, Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Bupropion, Esketamine', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, Brincidofovir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72833/', '[1] Sitsen JM, Maris FA, Timmer CJ "Concomitant use of mirtazapine and cimetidine: a drug-drug interaction study in healthy male subjects." Eur J Clin Pharmacol 56 (2000): 389-94[2] "Product Information. Remeron (mirtazapine)." Organon, West Orange, NJ.[3] Stormer E, von Moltke LL, Shader RI, Greenblatt DJ "Metabolism of the antidepressant mirtazapine in vitro: contribution of cytochromes P-450 1A2, 2D6, and 3A4" Drug Metab Dispos 28 (2000): 1168-75[4] Okubo M, Murayama N, Miura J, Chiba Y, Yamazaki H "Effects of cytochrome P450 2D6 and 3A5 genotypes and possible coadministered medicines on the metabolic clearance of antidepressant mirtazapine in Japanese patients." Biochem Pharmacol 93 (2015): 104-9[5] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[6] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[7] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[8] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72747, 'Cobicistat', 'Nafcillin', 'Moderate', 'Coadministration with moderate or weak inducers of CYP450 3A4 may reduce the plasma concentrations of cobicistat, which is primarily metabolized by the isoenzyme.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of antiretroviral regimens containing cobicistat with these inducers should be avoided.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of antiretroviral regimens containing cobicistat with these inducers should be avoided. Alternative treatment combinations that do not include metabolic inducers should be considered whenever possible. If concomitant use is required, close clinical and laboratory monitoring of antiretroviral response is recommended.', 'Metabolism', 'Sulbactam, Oxacillin, Dicloxacillin, Piperacillin, Meticillin, Carbenicillin, Bacampicillin, Ticarcillin, Mezlocillin, Tazobactam, Cloxacillin, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Telaprevir, Oseltamivir, Acyclovir, Brincidofovir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72834/', '[1] "Product Information. Genvoya (cobicistat/elvitegravir/emtricitabine/tenofov)." Gilead Sciences, Foster City, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Tybost (cobicistat)." Gilead Sciences, Foster City, CA.[4] Canadian Pharmacists Association "e-CPS. Available 