INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72792, 'Cobicistat', 'Ribociclib', 'Major', 'Coadministration with potent CYP450 3A4 inhibitors may increase the plasma concentrations and the risk of adverse effects of ribociclib, which is a substrate of the isoenzyme. The risk of adverse effects such as infections, neutropenia, leucopenia, anemia, thrombocytopenia, nausea, vomiting, diarrhea, stomatitis, anorexia, alopecia, fatigue, headache, and abnormal liver function may be increased.', NULL, 'Concomitant use of ribociclib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of ribociclib with potent CYP450 3A4 inhibitors should generally be avoided. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. If no alternatives exist, the dose of ribociclib should be reduced to 400 mg once daily. Patients receiving concomitant treatment with a CYP450 3A4 inhibitor should be monitored for the development of ribociclib-related adverse effects. Following discontinuation of the potent CYP450 3A4 inhibitor, the ribociclib dosage should be returned (after at least 5 half-lives of the inhibitor) to that used prior to initiation of the inhibitor.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Dolutegravir, Oseltamivir, Acyclovir, More', 'Lenvatinib, Trametinib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72879/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
