INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (36388, 'Riociguat', 'Avanafil', 'Major', 'Coadministration of riociguat with phosphodiesterase (PDE) inhibitors may cause significant hypotension. The mechanism involves peripheral vasodilation secondary to enhanced levels of cyclic guanosine monophosphate (cGMP) in vascular smooth muscle cells, as PDE inhibitors prevent degradation of cGMP while riociguat promotes its synthesis by stimulating soluble guanylate cyclase (sGC), an enzyme in the cardiopulmonary system that binds with nitric oxide (NO) to catalyze the synthesis of cGMP. Riociguat does not affect the pharmacokinetics of sildenafil.', NULL, 'Concomitant use of riociguat with PDE inhibitors, including specific PDE-5 inhibitors (e.', NULL, 'Concomitant use of riociguat with PDE inhibitors, including specific PDE-5 inhibitors (e.g., avanafil, sildenafil, tadalafil, vardenafil) or nonspecific PDE inhibitors (e.g., dipyridamole, theophylline), is considered contraindicated. Riociguat should be discontinued at least 24 hours before administering a PDE-5 inhibitor, and avoidance of riociguat administration within 24 hours after sildenafil or within 48 hours after tadalafil is recommended. Limited data exists for other PDE inhibitors.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/36444/', '[1] "Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc, West Haven, CT.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767363021);
