    ) VALUES (1345343, 'Amiodarone', 'Nadolol', 'Moderate', 'Additive effects of severe bradycardia, cardiac arrest, and ventricular fibrillation may occur in patients administered amiodarone and beta blockers. The mechanism may be related to additive slowing in AV conduction.', NULL, 'Clinical monitoring of patient hemodynamic status and response is recommended.', NULL, 'Clinical monitoring of patient hemodynamic status and response is recommended.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
