    ) VALUES (1345482, 'Amiodarone', 'Silodosin', 'Moderate', 'Coadministration with potent inhibitors of the P-glycoprotein (P-gp) efflux transporter may significantly increase the oral bioavailability of silodosin, which has been shown in vitro to be a substrate of P-gp.', NULL, 'Concomitant use of silodosin with potent P-gp inhibitors such as cyclosporine is not recommended.', NULL, 'Concomitant use of silodosin with potent P-gp inhibitors such as cyclosporine is not recommended.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
