        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1345530, 'Amiodarone', 'Thiopental', 'Moderate', 'Amiodarone may increase the effect of general anesthetics due to its adrenergic blocking effects. Data have been conflicting; however, the potential for increased risk of intraoperative complications such as arrhythmia, heart block, or pacemaker dependency should be considered.', NULL, 'Cardiorespiratory status should be closely monitored during surgery and until the effects of anesthesia have worn off.', NULL, 'Cardiorespiratory status should be closely monitored during surgery and until the effects of anesthesia have worn off.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
