INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (36777, 'Amprenavir', 'Axitinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of axitinib, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of axitinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of axitinib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of axitinib during and for 2 weeks after treatment with itraconazole. Alternative agents with no or minimal CYP450 3A4 inhibition potential are recommended whenever possible. Should concomitant treatment with a potent inhibitor be required, the manufacturer recommends that axitinib dosage be reduced by approximately one-half, as this is predicted to adjust the axitinib systemic exposure (AUC) to the range observed without inhibitors; however, clinical data are lacking. Subsequent dosage adjustments can be made according to individual therapeutic response and tolerability.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Futibatinib, Tucatinib, Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/36833/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] "Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[7] "Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (36778, 'Axitinib', 'Artesunate', 'Moderate', 'Coadministration with potent inhibitors of uridine diphosphate glucuronosyltransferase (UGT) may increase the peak plasma concentration (Cmax) and systemic exposure (AUC) of dihydroartemisinin (DHA), an active metabolite of artesunate and substrate of UGT 1A9 or 2B7. Increased exposure to artesunate may increase the risk of adverse effects such as anemia, increased liver transaminases, thrombocytopenia, hyperbilirubinemia, acute renal failure, and leukocytosis.', NULL, 'Monitoring for adverse reactions is recommended if artesunate is coadministered with potent UGT inhibitors.', NULL, 'Monitoring for adverse reactions is recommended if artesunate is coadministered with potent UGT inhibitors. Dosage adjustments or alternative treatments may be required based on clinical response and tolerance.', 'Others', 'Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, Selumetinib, Cobimetinib, More', 'Mefloquine, Lumefantrine, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Hydroxychloroquine, Halofantrine, Quinine, Primaquine, Proguanil, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/36834/', '[1] "Product Information. Artesunate (artesunate)." Amivas, Frederick, MD.[2] Shelton MJ, Wynn HE, Newitt RG, DiFrancesco R "Effects of grapefruit juice on pharmacokinetic exposure to indinavir in HIV-positive subjects." J Clin Pharmacol 41 (2001): 435-42[3] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point, PA.[4] Yeh KC, Deutsch PJ, Haddix H, Hesney M, Hoagland V, Ju WD, Justice SJ, Osborne