INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (36861, 'Axitinib', 'Mifepristone', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of axitinib, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of axitinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of axitinib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of axitinib during and for 2 weeks after treatment with itraconazole. Alternative agents with no or minimal CYP450 3A4 inhibition potential are recommended whenever possible. Should concomitant treatment with a potent inhibitor be required, the manufacturer recommends that axitinib dosage be reduced by approximately one-half, as this is predicted to adjust the axitinib systemic exposure (AUC) to the range observed without inhibitors; however, clinical data are lacking. Subsequent dosage adjustments can be made according to individual therapeutic response and tolerability.', 'Metabolism', 'Ulipristal, Ospemifene, Raloxifene, Bazedoxifene, Prasterone', 'Regorafenib, Alpelisib, Trametinib, Erlotinib, Erdafitinib, Afatinib, Dacomitinib, Tivozanib, Alectinib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/36917/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] "Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[5] Wohlt PD, Zheng L, Gunderson S, Bal