INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (37381, 'Warfarin', 'Azathioprine', 'Moderate', 'Limited clinical data suggest that the purine analog antimetabolites, azathioprine and mercaptopurine, may reduce the hypoprothrombinemic response to warfarin and other oral anticoagulants. There have been case reports of patients stabilized on oral anticoagulant therapy whose prothrombin time or INR decreased following addition of the antimetabolites and returned to normal only after their discontinuation or an increase in anticoagulant dosage.', NULL, 'Given the potential for interaction and the narrow therapeutic index of oral anticoagulants, patients should be closely monitored during concomitant therapy with azathioprine or mercaptopurine.', NULL, 'Given the potential for interaction and the narrow therapeutic index of oral anticoagulants, patients should be closely monitored during concomitant therapy with azathioprine or mercaptopurine. The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of antimetabolite therapy in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of blood clots (e.g. chest pain, shortness of breath, sudden loss of vision, or pain, redness or swelling in an extremity).', 'Antagonism', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, More', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Diroximel fumarate, Abatacept, Upadacitinib, Siponimod, Apremilast, Inebilizumab, Cladribine, Belumosudil, More', 'Moderate', NULL, 'DDInter', 'pharmacodynami