INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (37478, 'Drospirenone', 'Azilsartan medoxomil', 'Moderate', 'The use of drospirenone may potentiate the risk of hyperkalemia in patients receiving chronic treatment with drugs that can increase serum potassium such as angiotensin converting enzyme (ACE) inhibitors, angiotensin II receptor antagonists, potassium-sparing diuretics, aldosterone antagonists, nonsteroidal anti-inflammatory drugs (NSAIDs), potassium preparations, and miscellaneous agents like cyclosporine, heparin, tacrolimus, and trimethoprim. Drospirenone has antimineralocorticoid activity and may induce hyperkalemia in susceptible patients.', NULL, 'Serum potassium levels should be checked regularly during concomitant therapy with drospirenone and other agents that can increase serum potassium.', NULL, 'Serum potassium levels should be checked regularly during concomitant therapy with drospirenone and other agents that can increase serum potassium. Particular caution is warranted in patients with renal impairment, diabetes, old age, severe or worsening heart failure, or dehydration. Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Synergism', 'Ulipristal, Norethisterone, Norelgestromin, Etonogestrel, Megestrol acetate, Norgestimate, Etynodiol, Estradiol, Levonorgestrel, Medroxyprogesterone acetate, Desogestrel, More', 'Hydrochlorothiazide, Sacubitril, Aliskiren, Nebivolol', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/37534/', '[1] Ponce SP,