INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1345993, 'Amitriptyline', 'Clobazam', 'Moderate', 'Coadministration with clobazam may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 2D6. The mechanism is decreased clearance due to inhibition of CYP450 2D6 activity by clobazam.', 'DDInter', 1767317475, 'Caution is advised if clobazam must be used concomitantly with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever clobazam is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if clobazam must be used concomitantly with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1345994, 'Amitriptyline', 'Clofazimine', 'Moderate', 'Clofazimine may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317475, 'Caution is recommended if clofazimine is used in combination with other drugs that can prolong the QT interval. Consider monitoring electrocardiograms, particularly in patients receiving clofazimine dosages greater than 100 mg/day, and discontinue QT-prolonging medications if clinically significant ventricular arrhythmia is noted or if QTcF interval >=500 ms is observed. If syncope occurs, obtain an ECG to detect QT prolongation.', 'Synergism', 'Caution is recommended if clofazimine is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
